'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { MessageCircle, Send, Bot, User, Sparkles } from 'lucide-react';
import { aiBrainService } from '../services/ai-brain-service';

export default function AIChat() {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: 'ai',
      content:
        "Hello! I'm your AI trading assistant. Ask me anything about market analysis, stock recommendations, or trading strategies.",
      timestamp: new Date(),
    },
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessage = {
      id: messages.length + 1,
      type: 'user',
      content: inputMessage,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      // Try to extract a symbol (e.g. AAPL) from the message, fallback to SPY
      const symbolMatch = inputMessage.match(/\b[A-Z]{1,5}\b/);
      const symbol = symbolMatch ? symbolMatch[0] : 'SPY';
      const aiResponse = await aiBrainService.getIntelligentRecommendation(symbol);
      const aiMessage = {
        id: messages.length + 2,
        type: 'ai',
        content: `**AI Recommendation for ${symbol}:**\n\nAction: ${aiResponse.action}\nConfidence: ${(aiResponse.confidence * 100).toFixed(1)}%\nReasoning: ${Array.isArray(aiResponse.reasoning) ? aiResponse.reasoning.join('; ') : aiResponse.reasoning}\n\nPrice Targets: $${aiResponse.priceTargets.moderate.toFixed(2)} (mod)`,
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      setMessages(prev => [
        ...prev,
        {
          id: messages.length + 2,
          type: 'ai',
          content: 'Sorry, the AI is currently unavailable. Please try again later.',
          timestamp: new Date(),
        },
      ]);
    } finally {
      setIsLoading(false);
      setInputMessage('');
    }
  };

  if (!isOpen) {
    return (
      <div className="fixed bottom-6 right-6 z-50">
        <Button
          onClick={() => setIsOpen(true)}
          className="bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-600 hover:to-blue-600 rounded-full w-16 h-16 shadow-lg border border-cyan-400/30"
        >
          <MessageCircle className="h-8 w-8" />
        </Button>
      </div>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 z-50 w-96">
      <Card className="bg-gray-900/95 border-cyan-500/30 backdrop-blur-xl shadow-2xl">
        <div className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="text-white flex items-center">
              <Bot className="h-5 w-5 mr-2 text-cyan-400" />
              AI Assistant
              <Badge className="ml-2 bg-gradient-to-r from-cyan-500 to-blue-500">
                <Sparkles className="h-3 w-3 mr-1" />
                Live
              </Badge>
            </CardTitle>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsOpen(false)}
              className="text-gray-400 hover:text-white"
            >
              ×
            </Button>
          </div>
        </div>
        <CardContent className="space-y-4">
          {/* Messages */}
          <div className="h-64 overflow-y-auto space-y-3 pr-2">
            {messages.map((message: any) => (
              <div
                key={message.id}
                className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-[80%] p-3 rounded-lg ${
                    message.type === 'user'
                      ? 'bg-gradient-to-r from-cyan-500 to-blue-500 text-white'
                      : 'bg-gray-800/60 text-gray-100 border border-cyan-500/20'
                  }`}
                >
                  <div className="flex items-start space-x-2">
                    {message.type === 'ai' && (
                      <Bot className="h-4 w-4 text-cyan-400 mt-0.5 flex-shrink-0" />
                    )}
                    {message.type === 'user' && (
                      <User className="h-4 w-4 text-white mt-0.5 flex-shrink-0" />
                    )}
                    <p className="text-sm">{message.content}</p>
                  </div>
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="flex justify-start">
                <div className="max-w-[80%] p-3 rounded-lg bg-gray-800/60 text-gray-100 border border-cyan-500/20 animate-pulse">
                  <div className="flex items-start space-x-2">
                    <Bot className="h-4 w-4 text-cyan-400 mt-0.5 flex-shrink-0" />
                    <p className="text-sm">AI is thinking...</p>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Input */}
          <div className="flex space-x-2">
            <Input
              value={inputMessage}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setInputMessage(e.target.value)}
              onKeyPress={(e: React.KeyboardEvent<HTMLInputElement>) =>
                e.key === 'Enter' && sendMessage()
              }
              placeholder="Ask about stocks, analysis, or strategies..."
              className="bg-gray-800/40 border-cyan-500/30 text-white placeholder-gray-400"
            />
            <Button
              onClick={sendMessage}
              className="bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-600 hover:to-blue-600"
              disabled={isLoading}
            >
              <Send className="h-4 w-4" />
            </Button>
          </div>

          {/* Quick Actions */}
          <div className="flex flex-wrap gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setInputMessage('Analyze AAPL')}
              className="text-xs border-cyan-500/30 text-gray-300 hover:text-white"
            >
              Analyze AAPL
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setInputMessage('Market outlook')}
              className="text-xs border-cyan-500/30 text-gray-300 hover:text-white"
            >
              Market Outlook
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setInputMessage('Portfolio tips')}
              className="text-xs border-cyan-500/30 text-gray-300 hover:text-white"
            >
              Portfolio Tips
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { AdvancedAIAutoTrader } from '../services/ai-auto-trader-enhanced';

interface TradingSignal {
  symbol: string;
  action: string;
  confidence: number;
  reasoning?: string[];
}

interface Trade {
  symbol: string;
  action: 'buy' | 'sell';
  price: number;
  shares: number;
  time: string;
  reason: string;
}

interface Portfolio {
  totalValue: number;
  cash: number;
  holdings: Record<string, { shares: number; avgPrice: number }>;
  trades: Trade[];
  totalReturn?: number;
  dailyPnL?: number;
  performance?: {
    totalReturn: number;
    dailyReturn: number;
    weeklyReturn: number;
    monthlyReturn: number;
    sharpeRatio: number;
    maxDrawdown?: number;
    winRate: number;
    lastUpdated: Date;
  };
}

interface Performance {
  totalReturn: number;
  dailyPnL: number;
  winRate: number;
  sharpeRatio: number;
}

interface Analysis {
  signals?: TradingSignal[];
  marketCondition?: string;
  volatility?: number;
  lastTradeResult?: {
    success: boolean;
    message: string;
    trades?: any[];
  };
  recommendations?: string[];
  riskAnalysis?: {
    riskScore: number;
    volatility: number;
    beta?: number;
  };
}

interface DashboardState {
  trader: AdvancedAIAutoTrader | null;
  portfolio: Portfolio | null;
  isTrading: boolean;
  performance: Performance | null;
  analysis: Analysis | null;
  loading: boolean;
  error: string | null;
}

export default function AITradingDashboard() {
  const [state, setState] = useState<DashboardState>({
    trader: null,
    portfolio: null,
    isTrading: false,
    performance: null,
    analysis: null,
    loading: true,
    error: null,
  });

  const [selectedSymbols] = useState(['AAPL', 'TSLA', 'MSFT', 'GOOGL', 'AMZN']);
  const [riskLevel, setRiskLevel] = useState<'LOW' | 'MEDIUM' | 'HIGH'>('MEDIUM');

  const initializeTrader = useCallback(async () => {
    try {
      const trader = new AdvancedAIAutoTrader(50000, riskLevel);
      const portfolio = trader.getPortfolio();

      setState(prev => ({
        ...prev,
        trader,
        portfolio,
        loading: false,
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Failed to initialize AI trader',
        loading: false,
      }));
    }
  }, [riskLevel]);

  useEffect(() => {
    initializeTrader();
  }, [initializeTrader]);

  const runAIAnalysis = async () => {
    if (!state.trader) return;

    setState(prev => ({ ...prev, loading: true }));

    try {
      const analysis = await state.trader.runAIAnalysis(selectedSymbols);
      const portfolio = state.trader.getPortfolio();

      setState(prev => ({
        ...prev,
        analysis,
        portfolio,
        loading: false,
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Analysis failed',
        loading: false,
      }));
    }
  };

  const executeAITrading = async () => {
    if (!state.trader) return;

    setState(prev => ({ ...prev, isTrading: true }));

    try {
      const result = await state.trader.executeAITrading(selectedSymbols);
      const portfolio = state.trader.getPortfolio();

      setState(prev => ({
        ...prev,
        portfolio,
        isTrading: false,
        analysis: { ...prev.analysis, lastTradeResult: result },
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Trading execution failed',
        isTrading: false,
      }));
    }
  };

  const emergencyStop = () => {
    if (state.trader) {
      state.trader.emergencyStop();
      setState(prev => ({ ...prev, isTrading: false }));
    }
  };

  if (state.loading && !state.trader) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-black text-white">
        <div className="container mx-auto px-4 py-20">
          <div className="flex items-center justify-center">
            <div className="text-center">
              <div className="animate-spin w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-4"></div>
              <h2 className="text-xl font-semibold">Initializing AI Trading Engine...</h2>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-black text-white">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            AI Trading Dashboard
          </h1>
          <p className="text-gray-300">Advanced AI-powered trading with real-time analysis</p>
        </div>

        {/* Control Panel */}
        <Card className="p-6 mb-6 bg-gray-900/50 border-gray-700">
          <div className="flex flex-wrap gap-4 items-center justify-between">
            <div className="flex gap-4 items-center">
              <Button
                onClick={runAIAnalysis}
                disabled={state.loading}
                className="bg-blue-600 hover:bg-blue-700"
              >
                {state.loading ? 'Analyzing...' : 'Run AI Analysis'}
              </Button>

              <Button
                onClick={executeAITrading}
                disabled={state.isTrading || !state.analysis}
                className="bg-green-600 hover:bg-green-700"
              >
                {state.isTrading ? 'Trading...' : 'Execute AI Trading'}
              </Button>

              <Button
                onClick={emergencyStop}
                variant="destructive"
                className="bg-red-600 hover:bg-red-700"
              >
                Emergency Stop
              </Button>
            </div>

            <div className="flex gap-4 items-center">
              <select
                value={riskLevel}
                onChange={e => setRiskLevel(e.target.value as 'LOW' | 'MEDIUM' | 'HIGH')}
                className="px-3 py-2 bg-gray-800 border border-gray-600 rounded text-white"
              >
                <option value="LOW">Low Risk</option>
                <option value="MEDIUM">Medium Risk</option>
                <option value="HIGH">High Risk</option>
              </select>
            </div>
          </div>
        </Card>

        <Tabs defaultValue="portfolio" className="space-y-6">
          <TabsList className="grid w-full grid-cols-5 bg-gray-800">
            <TabsTrigger value="portfolio">Portfolio</TabsTrigger>
            <TabsTrigger value="analysis">AI Analysis</TabsTrigger>
            <TabsTrigger value="trades">Trade History</TabsTrigger>
            <TabsTrigger value="performance">Performance</TabsTrigger>
            <TabsTrigger value="strategies">Strategies</TabsTrigger>
          </TabsList>

          {/* Portfolio Tab */}
          <TabsContent value="portfolio">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Portfolio Overview */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Portfolio Overview</h3>
                {state.portfolio && (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <p className="text-sm text-gray-400">Total Value</p>
                        <p className="text-2xl font-bold text-green-400">
                          ${state.portfolio.totalValue?.toFixed(2) || '0.00'}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Cash</p>
                        <p className="text-xl font-semibold">
                          ${state.portfolio.cash?.toFixed(2) || '0.00'}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Total Return</p>
                        <p
                          className={`text-xl font-semibold ${
                            (state.portfolio.totalReturn || 0) >= 0
                              ? 'text-green-400'
                              : 'text-red-400'
                          }`}
                        >
                          {(state.portfolio.totalReturn || 0).toFixed(2)}%
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Daily P&L</p>
                        <p
                          className={`text-xl font-semibold ${
                            (state.portfolio.dailyPnL || 0) >= 0 ? 'text-green-400' : 'text-red-400'
                          }`}
                        >
                          ${(state.portfolio.dailyPnL || 0).toFixed(2)}
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </Card>

              {/* Current Holdings */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Current Holdings</h3>
                {state.portfolio?.holdings && Object.keys(state.portfolio.holdings).length > 0 ? (
                  <div className="space-y-3">
                    {Object.entries(state.portfolio.holdings).map(
                      ([symbol, holding]: [string, any]) => (
                        <div
                          key={symbol}
                          className="flex justify-between items-center p-3 bg-gray-800 rounded"
                        >
                          <div>
                            <p className="font-semibold">{symbol}</p>
                            <p className="text-sm text-gray-400">{holding.shares} shares</p>
                          </div>
                          <div className="text-right">
                            <p className="font-semibold">${holding.avgPrice?.toFixed(2)}</p>
                            <p className="text-sm text-gray-400">Avg Cost</p>
                          </div>
                        </div>
                      )
                    )}
                  </div>
                ) : (
                  <p className="text-gray-400">No current holdings</p>
                )}
              </Card>
            </div>
          </TabsContent>

          {/* AI Analysis Tab */}
          <TabsContent value="analysis">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* AI Signals */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">AI Trading Signals</h3>
                {state.analysis?.signals?.length ? (
                  <div className="space-y-3">
                    {state.analysis?.signals?.map((signal: TradingSignal, index: number) => (
                      <div key={index} className="p-3 bg-gray-800 rounded">
                        <div className="flex justify-between items-start mb-2">
                          <span className="font-semibold">{signal.symbol}</span>
                          <span
                            className={`px-2 py-1 rounded text-sm ${
                              signal.action === 'BUY' ? 'bg-green-600' : 'bg-red-600'
                            }`}
                          >
                            {signal.action}
                          </span>
                        </div>
                        <div className="text-sm space-y-1">
                          <p>Confidence: {(signal.confidence * 100).toFixed(1)}%</p>
                          <Progress value={signal.confidence * 100} className="h-2" />
                          <p className="text-gray-400">
                            {signal.reasoning?.slice(0, 2).join(', ')}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-400">Run AI analysis to see signals</p>
                )}
              </Card>

              {/* AI Recommendations */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">AI Recommendations</h3>
                {state.analysis?.recommendations?.length ? (
                  <div className="space-y-2">
                    {state.analysis?.recommendations?.map((rec: string, index: number) => (
                      <div
                        key={index}
                        className="p-3 bg-blue-900/30 border border-blue-700 rounded"
                      >
                        <p className="text-sm">{rec}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-400">No recommendations available</p>
                )}
              </Card>
            </div>
          </TabsContent>

          {/* Trade History Tab */}
          <TabsContent value="trades">
            <Card className="p-6 bg-gray-900/50 border-gray-700">
              <h3 className="text-xl font-semibold mb-4">Recent Trades</h3>
              {state.portfolio?.trades?.length ? (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="border-b border-gray-700">
                        <th className="text-left p-2">Symbol</th>
                        <th className="text-left p-2">Action</th>
                        <th className="text-left p-2">Shares</th>
                        <th className="text-left p-2">Price</th>
                        <th className="text-left p-2">Time</th>
                        <th className="text-left p-2">Reason</th>
                      </tr>
                    </thead>
                    <tbody>
                      {state.portfolio?.trades
                        ?.slice(-10)
                        .reverse()
                        .map((trade: Trade, index: number) => (
                          <tr key={index} className="border-b border-gray-800">
                            <td className="p-2 font-semibold">{trade.symbol}</td>
                            <td className="p-2">
                              <span
                                className={`px-2 py-1 rounded text-xs ${
                                  trade.action === 'buy' ? 'bg-green-600' : 'bg-red-600'
                                }`}
                              >
                                {trade.action.toUpperCase()}
                              </span>
                            </td>
                            <td className="p-2">{trade.shares}</td>
                            <td className="p-2">${trade.price.toFixed(2)}</td>
                            <td className="p-2 text-sm text-gray-400">
                              {new Date(trade.time).toLocaleTimeString()}
                            </td>
                            <td className="p-2 text-sm text-gray-400 max-w-xs truncate">
                              {trade.reason}
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <p className="text-gray-400">No trades executed yet</p>
              )}
            </Card>
          </TabsContent>

          {/* Performance Tab */}
          <TabsContent value="performance">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Performance Metrics</h3>
                {state.portfolio?.performance && (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <p className="text-sm text-gray-400">Total Return</p>
                        <p className="text-xl font-semibold text-green-400">
                          {state.portfolio.performance.totalReturn?.toFixed(2)}%
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Sharpe Ratio</p>
                        <p className="text-xl font-semibold">
                          {state.portfolio.performance.sharpeRatio?.toFixed(2)}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Max Drawdown</p>
                        <p className="text-xl font-semibold text-red-400">
                          {state.portfolio?.performance?.maxDrawdown
                            ? (state.portfolio.performance.maxDrawdown * 100).toFixed(2) + '%'
                            : 'N/A'}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Win Rate</p>
                        <p className="text-xl font-semibold">
                          {state.portfolio.performance.winRate?.toFixed(1)}%
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </Card>

              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Risk Analysis</h3>
                {state.analysis?.riskAnalysis && (
                  <div className="space-y-4">
                    <div>
                      <p className="text-sm text-gray-400">Risk Score</p>
                      <div className="flex items-center gap-2">
                        <Progress
                          value={state.analysis.riskAnalysis.riskScore}
                          className="flex-1 h-3"
                        />
                        <span className="text-sm">{state.analysis.riskAnalysis.riskScore}/100</span>
                      </div>
                    </div>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <p className="text-gray-400">Volatility</p>
                        <p>{(state.analysis.riskAnalysis.volatility * 100).toFixed(1)}%</p>
                      </div>
                      <div>
                        <p className="text-gray-400">Beta</p>
                        <p>{state.analysis.riskAnalysis.beta?.toFixed(2)}</p>
                      </div>
                    </div>
                  </div>
                )}
              </Card>
            </div>
          </TabsContent>

          {/* Strategies Tab */}
          <TabsContent value="strategies">
            <Card className="p-6 bg-gray-900/50 border-gray-700">
              <h3 className="text-xl font-semibold mb-4">Active AI Strategies</h3>
              {state.trader && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {state.trader.getStrategies().map((strategy: any) => (
                    <div key={strategy.id} className="p-4 bg-gray-800 rounded">
                      <div className="flex justify-between items-start mb-2">
                        <h4 className="font-semibold">{strategy.name}</h4>
                        <span
                          className={`px-2 py-1 rounded text-xs ${
                            strategy.status === 'ACTIVE' ? 'bg-green-600' : 'bg-gray-600'
                          }`}
                        >
                          {strategy.status}
                        </span>
                      </div>
                      <p className="text-sm text-gray-400 mb-3">{strategy.description}</p>
                      <div className="grid grid-cols-2 gap-2 text-xs">
                        <div>
                          <p className="text-gray-400">Return</p>
                          <p className="text-green-400">{strategy.performance.totalReturn}%</p>
                        </div>
                        <div>
                          <p className="text-gray-400">Win Rate</p>
                          <p>{strategy.performance.winRate}%</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

