'use client';

import { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Mic, MicOff, Volume2, VolumeX, Brain, MessageCircle, Zap, Settings } from 'lucide-react';

interface ConversationItem {
  id: number;
  role: string;
  message: string;
  timestamp: Date;
}

declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

interface SpeechRecognitionAlternative {
  transcript: string;
  confidence: number;
}

interface SpeechRecognitionResult {
  readonly length: number;
  item(index: number): SpeechRecognitionAlternative;
  [index: number]: SpeechRecognitionAlternative;
  isFinal: boolean;
}

interface SpeechRecognitionResultList {
  readonly length: number;
  item(index: number): SpeechRecognitionResult;
  [index: number]: SpeechRecognitionResult;
}

interface SpeechRecognitionEvent extends Event {
  readonly resultIndex: number;
  readonly results: SpeechRecognitionResultList;
}

interface SpeechRecognition extends EventTarget {
  continuous: boolean;
  interimResults: boolean;
  lang: string;
  start(): void;
  stop(): void;
  abort(): void;
  onstart: ((this: SpeechRecognition, ev: Event) => any) | null;
  onend: ((this: SpeechRecognition, ev: Event) => any) | null;
  onresult: ((this: SpeechRecognition, ev: SpeechRecognitionEvent) => any) | null;
  onerror: ((this: SpeechRecognition, ev: Event) => any) | null;
}

declare const SpeechRecognition: {
  prototype: SpeechRecognition;
  new (): SpeechRecognition;
};

export default function AIVoiceAssistant() {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const [conversation, setConversation] = useState<ConversationItem[]>([]);
  const [aiPersonality, setAiPersonality] = useState('professional');

  const recognitionRef = useRef<SpeechRecognition | null>(null);
  const synthRef = useRef<SpeechSynthesis | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [aiThinking, setAiThinking] = useState(false);
  const [soundWave, setSoundWave] = useState(Array(20).fill(0));

  useEffect(() => {
    // Initialize speech recognition
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognitionConstructor =
        window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognitionConstructor();

      if (recognitionRef.current) {
        recognitionRef.current.continuous = true;
        recognitionRef.current.interimResults = true;
        recognitionRef.current.lang = 'en-US';

        recognitionRef.current.onresult = (event: SpeechRecognitionEvent) => {
          const current = event.resultIndex;
          const transcript = event.results[current][0].transcript;
          setTranscript(transcript);

          if (event.results[current].isFinal) {
            processVoiceCommand(transcript);
          }
        };

        recognitionRef.current.onend = () => {
          setIsListening(false);
        };
      }
    }

    // Initialize speech synthesis
    if ('speechSynthesis' in window) {
      synthRef.current = window.speechSynthesis;
    }

    // Simulate connection status
    setIsConnected(true);

    // Add initial greeting
    addToConversation(
      'assistant',
      "Hello! I'm your AI trading assistant. Ask me about market analysis, stock recommendations, or trading strategies."
    );
  }, []);

  // Sound wave animation effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isListening || isSpeaking) {
      interval = setInterval(() => {
        setSoundWave(prev => prev.map(() => Math.random() * 100));
      }, 100);
    } else {
      setSoundWave(Array(20).fill(0));
    }
    return () => clearInterval(interval);
  }, [isListening, isSpeaking]);

  const startListening = () => {
    if (recognitionRef.current && !isListening) {
      setIsListening(true);
      setTranscript('');
      recognitionRef.current.start();
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const processVoiceCommand = async (command: string) => {
    addToConversation('user', command);
    setAiThinking(true);

    // Simulate AI processing with visual feedback
    const aiResponse = await generateAIResponse(command.toLowerCase());
    setAiThinking(false);
    addToConversation('assistant', aiResponse);

    if (voiceEnabled) {
      speakResponse(aiResponse);
    }
  };

  const generateAIResponse = async (command: string) => {
    // Simulate processing delay with animation
    await new Promise(resolve => setTimeout(resolve, 1500));

    // Enhanced AI response generation
    if (command.includes('price') || command.includes('stock')) {
      const stocks = ['AAPL', 'MSFT', 'GOOGL', 'AMZN', 'TSLA'];
      const stock = stocks[Math.floor(Math.random() * stocks.length)];
      const price = (150 + Math.random() * 200).toFixed(2);
      const change = (Math.random() - 0.5) * 10;
      return `${stock} is currently trading at $${price}, ${change >= 0 ? 'up' : 'down'} ${Math.abs(change).toFixed(2)}% today. Volume is ${(Math.random() * 50 + 10).toFixed(1)}M shares.`;
    }

    if (command.includes('market') || command.includes('outlook')) {
      const sentiment = Math.random() > 0.5 ? 'bullish' : 'bearish';
      const confidence = (70 + Math.random() * 30).toFixed(0);
      return `Current market sentiment is ${sentiment} with ${confidence}% confidence. The S&P 500 is showing ${Math.random() > 0.5 ? 'strength' : 'consolidation'} patterns.`;
    }

    if (command.includes('portfolio') || command.includes('allocation')) {
      return `Based on current market conditions, I recommend a balanced allocation: 60% equities, 25% bonds, 10% commodities, and 5% cash. This provides optimal risk-adjusted returns.`;
    }

    // Default responses with more personality
    const responses = [
      'Fascinating question! My neural networks are processing multiple market indicators and sentiment data to provide you with the most accurate analysis.',
      'Excellent timing on that query. Based on my real-time analysis of over 10,000 data points, I can provide specific recommendations tailored to your investment profile.',
      'My quantum-enhanced algorithms are analyzing market microstructure, options flow, and institutional positioning to identify alpha opportunities.',
      "Outstanding question! I'm cross-referencing technical patterns, fundamental metrics, and macro-economic indicators to deliver precise insights.",
    ];

    return responses[Math.floor(Math.random() * responses.length)];
  };

  const speakResponse = (text: string) => {
    if (synthRef.current && voiceEnabled) {
      setIsSpeaking(true);
      const utterance = new SpeechSynthesisUtterance(text);

      utterance.rate = aiPersonality === 'energetic' ? 1.2 : 0.9;
      utterance.pitch = aiPersonality === 'friendly' ? 1.1 : 1.0;
      utterance.volume = 0.8;

      utterance.onend = () => {
        setIsSpeaking(false);
      };

      synthRef.current.speak(utterance);
    }
  };

  const addToConversation = (role: string, message: string) => {
    const newMessage = {
      id: Date.now(),
      role,
      message,
      timestamp: new Date(),
    };
    setConversation(prev => [newMessage, ...prev.slice(0, 19)]);
  };

  const toggleVoice = () => {
    setVoiceEnabled(!voiceEnabled);
    if (isSpeaking && synthRef.current) {
      synthRef.current.cancel();
      setIsSpeaking(false);
    }
  };

  return (
    <div className="space-y-6">
      <Card className="bg-black/20 border-purple-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-white flex items-center">
            <Brain className="h-6 w-6 mr-3 text-purple-400" />
            AI Voice Trading Assistant
            <Badge className="ml-3 bg-gradient-to-r from-purple-500 to-pink-500">
              <Zap className="h-3 w-3 mr-1" />
              NEURAL POWERED
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="space-y-2">
              <Button
                onClick={isListening ? stopListening : startListening}
                className={`w-full ${
                  isListening
                    ? 'bg-red-500 hover:bg-red-600 animate-pulse'
                    : 'bg-green-500 hover:bg-green-600'
                }`}
              >
                {isListening ? (
                  <>
                    <MicOff className="h-4 w-4 mr-2" />
                    Stop Listening
                  </>
                ) : (
                  <>
                    <Mic className="h-4 w-4 mr-2" />
                    Start Listening
                  </>
                )}
              </Button>
              {isListening && (
                <div className="text-center">
                  <Badge className="bg-red-500 animate-pulse">LISTENING</Badge>
                </div>
              )}
            </div>

            <div className="space-y-2">
              <Button
                onClick={toggleVoice}
                variant="outline"
                className={`w-full border-purple-500/30 ${
                  voiceEnabled ? 'text-purple-400' : 'text-gray-400'
                }`}
              >
                {voiceEnabled ? (
                  <>
                    <Volume2 className="h-4 w-4 mr-2" />
                    Voice On
                  </>
                ) : (
                  <>
                    <VolumeX className="h-4 w-4 mr-2" />
                    Voice Off
                  </>
                )}
              </Button>
              {isSpeaking && (
                <div className="text-center">
                  <Badge className="bg-blue-500 animate-pulse">SPEAKING</Badge>
                </div>
              )}
            </div>

            <div className="space-y-2">
              <select
                value={aiPersonality}
                onChange={e => setAiPersonality(e.target.value)}
                className="w-full bg-gray-800 border border-purple-500/30 rounded px-3 py-2 text-white"
              >
                <option value="professional">Professional</option>
                <option value="friendly">Friendly</option>
                <option value="energetic">Energetic</option>
              </select>
              <div className="text-center">
                <Badge className="bg-purple-500">AI PERSONALITY</Badge>
              </div>
            </div>

            <div className="space-y-2">
              <div className="text-center p-2 bg-gray-800/50 rounded">
                <div className="text-green-400 font-bold">ONLINE</div>
                <div className="text-xs text-gray-400">AI Ready</div>
              </div>
              <div className="text-center">
                <Badge className="bg-green-500">CONNECTED</Badge>
              </div>
            </div>
          </div>

          {transcript && (
            <div className="mt-4 p-3 bg-gray-800/50 rounded-lg border border-purple-500/30">
              <div className="text-purple-400 text-sm font-medium mb-1">Current Input:</div>
              <div className="text-white">{transcript}</div>
            </div>
          )}

          {/* Sound Wave Visualization */}
          {(isListening || isSpeaking) && (
            <div className="mt-4 p-4 bg-gradient-to-r from-purple-900/50 to-pink-900/50 rounded-lg border border-purple-500/30">
              <div className="text-center mb-2">
                <Badge className={`${isListening ? 'bg-red-500' : 'bg-blue-500'} animate-pulse`}>
                  {isListening ? 'LISTENING...' : 'SPEAKING...'}
                </Badge>
              </div>
              <div className="flex items-center justify-center space-x-1 h-8">
                {soundWave.map((height, index) => (
                  <div
                    key={index}
                    className={`bg-gradient-to-t ${
                      isListening ? 'from-red-400 to-red-600' : 'from-blue-400 to-blue-600'
                    } rounded-full transition-all duration-100`}
                    style={{
                      width: '3px',
                      height: `${Math.max(4, height / 4)}px`,
                      animation: `pulse ${0.5 + Math.random() * 0.5}s infinite`,
                    }}
                  />
                ))}
              </div>
            </div>
          )}

          {/* AI Thinking Indicator */}
          {aiThinking && (
            <div className="mt-4 p-4 bg-gradient-to-r from-purple-900/50 to-blue-900/50 rounded-lg border border-purple-500/30">
              <div className="flex items-center justify-center space-x-3">
                <div className="relative">
                  <Brain className="h-8 w-8 text-purple-400 animate-pulse" />
                  <div className="absolute -top-1 -right-1 h-3 w-3 bg-green-400 rounded-full animate-ping"></div>
                </div>
                <div className="text-purple-400 font-medium">AI Processing...</div>
                <div className="flex space-x-1">
                  {[0, 1, 2].map(i => (
                    <div
                      key={i}
                      className="w-2 h-2 bg-purple-400 rounded-full animate-bounce"
                      style={{ animationDelay: `${i * 0.1}s` }}
                    />
                  ))}
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <Card className="bg-black/20 border-purple-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-white flex items-center">
            <MessageCircle className="h-5 w-5 mr-2 text-purple-400" />
            Conversation History
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3 max-h-96 overflow-y-auto">
            {conversation.map((message: ConversationItem) => (
              <div
                key={message.id}
                className={`p-3 rounded-lg ${
                  message.role === 'user'
                    ? 'bg-blue-500/20 border border-blue-500/30 ml-8'
                    : 'bg-purple-500/20 border border-purple-500/30 mr-8'
                }`}
              >
                <div className="flex items-center justify-between mb-1">
                  <Badge className={message.role === 'user' ? 'bg-blue-500' : 'bg-purple-500'}>
                    {message.role === 'user' ? 'YOU' : 'AI ASSISTANT'}
                  </Badge>
                  <span className="text-xs text-gray-400">
                    {message.timestamp.toLocaleTimeString()}
                  </span>
                </div>
                <div className="text-white text-sm">{message.message}</div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      <Card className="bg-black/20 border-purple-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-white flex items-center">
            <Settings className="h-5 w-5 mr-2 text-purple-400" />
            Quick Commands
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
            {[
              'Check AAPL stock price',
              'Market outlook today',
              'Portfolio allocation advice',
              'Risk analysis',
              'Trading opportunities',
              'Sector recommendations',
            ].map((command, index) => (
              <Button
                key={index}
                onClick={() => processVoiceCommand(command)}
                variant="outline"
                size="sm"
                className="border-purple-500/30 text-purple-400 hover:bg-purple-500/20"
              >
                {command}
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import {
  Eye,
  Camera,
  Scan,
  Target,
  Zap,
  Brain,
  BarChart3,
  Activity,
  Layers,
  ImageIcon,
  Search,
} from 'lucide-react';

interface VisionModel {
  id: number;
  name: string;
  type: string;
  accuracy: number;
  specialty: string;
  status: string;
  confidence: number;
  processing: string;
}

interface ChartPattern {
  id: number;
  pattern: string;
  confidence: number;
  coordinates: {
    x: number;
    y: number;
    width: number;
    height: number;
  };
  direction: string;
  target: number;
  probability: number;
  timeframe: string;
}

interface AnalysisResult {
  id: number;
  type: string;
  value?: number;
  strength?: number;
  touches?: number;
  confidence: number;
  color: string;
  slope?: number | string;
  points?: Array<{ x: number; y: number }> | number;
  location?: string;
  magnitude?: number;
  significance?: number;
  direction?: string;
}

export default function ComputerVisionAnalysis() {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState<AnalysisResult[]>([]);
  const [chartPatterns, setChartPatterns] = useState<ChartPattern[]>([]);
  const [visionModels, setVisionModels] = useState<VisionModel[]>([]);
  const [processingProgress, setProcessingProgress] = useState(0);
  const [selectedImage, setSelectedImage] = useState<string | null>(null);

  const fileInputRef = useRef<HTMLInputElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    initializeVisionModels();
    generateMockAnalysis();
  }, []);

  const initializeVisionModels = () => {
    const models = [
      {
        id: 1,
        name: 'Chart Pattern CNN',
        type: 'Convolutional Neural Network',
        accuracy: 96.8,
        specialty: 'Technical Pattern Recognition',
        status: 'active',
        confidence: 94.2,
        processing: 'Real-time Chart Analysis',
      },
      {
        id: 2,
        name: 'Candlestick Vision AI',
        type: 'Deep Learning Classifier',
        accuracy: 93.5,
        specialty: 'Candlestick Pattern Detection',
        status: 'active',
        confidence: 91.7,
        processing: 'Price Action Analysis',
      },
      {
        id: 3,
        name: 'Support/Resistance Detector',
        type: 'Object Detection Model',
        accuracy: 95.1,
        specialty: 'Key Level Identification',
        status: 'active',
        confidence: 89.3,
        processing: 'Level Detection',
      },
      {
        id: 4,
        name: 'Volume Profile Analyzer',
        type: 'Semantic Segmentation',
        accuracy: 92.7,
        specialty: 'Volume Analysis',
        status: 'training',
        confidence: 87.9,
        processing: 'Volume Pattern Recognition',
      },
    ];

    setVisionModels(models);
  };

  const generateMockAnalysis = () => {
    const patterns = [
      {
        id: 1,
        pattern: 'Head and Shoulders',
        confidence: 94.2,
        coordinates: { x: 150, y: 200, width: 300, height: 150 },
        direction: 'bearish',
        target: 165.5,
        probability: 87,
        timeframe: '4H',
      },
      {
        id: 2,
        pattern: 'Ascending Triangle',
        confidence: 91.8,
        coordinates: { x: 500, y: 180, width: 250, height: 120 },
        direction: 'bullish',
        target: 285.75,
        probability: 83,
        timeframe: '1D',
      },
      {
        id: 3,
        pattern: 'Double Bottom',
        confidence: 88.5,
        coordinates: { x: 800, y: 250, width: 200, height: 100 },
        direction: 'bullish',
        target: 195.25,
        probability: 79,
        timeframe: '1H',
      },
    ];

    setChartPatterns(patterns);

    const results = [
      {
        id: 1,
        type: 'Support Level',
        value: 175.5,
        strength: 92,
        touches: 5,
        confidence: 96.3,
        color: 'green',
      },
      {
        id: 2,
        type: 'Resistance Level',
        value: 185.75,
        strength: 88,
        touches: 3,
        confidence: 91.7,
        color: 'red',
      },
      {
        id: 3,
        type: 'Trend Line',
        slope: 'ascending',
        strength: 85,
        points: 4,
        confidence: 89.2,
        color: 'blue',
      },
      {
        id: 4,
        type: 'Volume Spike',
        location: 'breakout zone',
        magnitude: 340,
        significance: 94,
        confidence: 93.8,
        color: 'purple',
      },
    ];

    setAnalysisResults(results);
  };

  const handleImageUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: ProgressEvent<FileReader>) => {
        if (e.target?.result) {
          setSelectedImage(e.target.result as string);
          analyzeChart(e.target.result as string);
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const analyzeChart = async (imageData: string) => {
    setIsAnalyzing(true);
    setProcessingProgress(0);

    // Simulate AI processing with progress updates
    const progressInterval = setInterval(() => {
      setProcessingProgress(prev => {
        if (prev >= 100) {
          clearInterval(progressInterval);
          setIsAnalyzing(false);
          return 100;
        }
        return prev + Math.random() * 15;
      });
    }, 200);

    // Simulate analysis completion
    setTimeout(() => {
      generateMockAnalysis();
      setIsAnalyzing(false);
      setProcessingProgress(100);
    }, 3000);
  };

  const drawAnalysisOverlay = () => {
    if (!canvasRef.current || !selectedImage) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw detected patterns
    chartPatterns.forEach(pattern => {
      ctx.strokeStyle = pattern.direction === 'bullish' ? '#10B981' : '#EF4444';
      ctx.lineWidth = 3;
      ctx.setLineDash([5, 5]);
      ctx.strokeRect(
        pattern.coordinates.x,
        pattern.coordinates.y,
        pattern.coordinates.width,
        pattern.coordinates.height
      );

      // Add label
      ctx.fillStyle = pattern.direction === 'bullish' ? '#10B981' : '#EF4444';
      ctx.font = '14px Arial';
      ctx.fillText(
        `${pattern.pattern} (${pattern.confidence.toFixed(1)}%)`,
        pattern.coordinates.x,
        pattern.coordinates.y - 10
      );
    });
  };

  useEffect(() => {
    if (selectedImage && chartPatterns.length > 0) {
      drawAnalysisOverlay();
    }
  }, [selectedImage, chartPatterns]);

  return (
    <div className="space-y-6">
      {/* Computer Vision Control Panel */}
      <Card className="bg-gradient-to-r from-blue-900/20 to-indigo-900/20 border-blue-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-white flex items-center">
            <Eye className="h-6 w-6 mr-2 text-blue-400" />
            Computer Vision Analysis
            <Badge className="ml-3 bg-gradient-to-r from-blue-500 to-indigo-500">
              <Camera className="h-3 w-3 mr-1" />
              AI Vision
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* Upload Section */}
            <div className="space-y-4">
              <h4 className="text-white font-semibold">Chart Upload</h4>

              <div className="space-y-3">
                <Button
                  onClick={() => fileInputRef.current?.click()}
                  className="w-full bg-blue-500 hover:bg-blue-600"
                  disabled={isAnalyzing}
                >
                  <ImageIcon className="h-4 w-4 mr-2" />
                  Upload Chart Image
                </Button>

                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />

                {isAnalyzing && (
                  <div className="p-3 bg-blue-500/10 rounded-lg border border-blue-500/30">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-blue-400 text-sm">Analyzing Chart...</span>
                      <span className="text-white text-sm">{processingProgress.toFixed(0)}%</span>
                    </div>
                    <Progress value={processingProgress} className="h-2" />
                  </div>
                )}

                <div className="text-xs text-gray-400">
                  Supported formats: JPG, PNG, GIF
                  <br />
                  Max size: 10MB
                </div>
              </div>
            </div>

            {/* Vision Models Status */}
            <div className="space-y-4">
              <h4 className="text-white font-semibold">Active Vision Models</h4>

              <div className="space-y-2">
                {visionModels.slice(0, 3).map((model: any) => (
                  <div
                    key={model.id}
                    className="p-3 bg-indigo-500/10 rounded-lg border border-indigo-500/30"
                  >
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-indigo-400 text-sm font-medium">{model.name}</span>
                      <Badge
                        className={model.status === 'active' ? 'bg-green-500' : 'bg-yellow-500'}
                      >
                        {model.status.toUpperCase()}
                      </Badge>
                    </div>
                    <div className="flex justify-between text-xs text-gray-400">
                      <span>Accuracy: {model.accuracy}%</span>
                      <span>Confidence: {model.confidence}%</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Analysis Statistics */}
            <div className="space-y-4">
              <h4 className="text-white font-semibold">Analysis Statistics</h4>

              <div className="grid grid-cols-2 gap-3">
                <div className="text-center p-3 bg-green-500/10 rounded-lg border border-green-500/30">
                  <Scan className="h-6 w-6 text-green-400 mx-auto mb-1" />
                  <p className="text-sm text-gray-400">Patterns Detected</p>
                  <p className="text-lg font-bold text-green-400">{chartPatterns.length}</p>
                </div>

                <div className="text-center p-3 bg-purple-500/10 rounded-lg border border-purple-500/30">
                  <Target className="h-6 w-6 text-purple-400 mx-auto mb-1" />
                  <p className="text-sm text-gray-400">Key Levels</p>
                  <p className="text-lg font-bold text-purple-400">{analysisResults.length}</p>
                </div>

                <div className="text-center p-3 bg-yellow-500/10 rounded-lg border border-yellow-500/30">
                  <Brain className="h-6 w-6 text-yellow-400 mx-auto mb-1" />
                  <p className="text-sm text-gray-400">Avg Confidence</p>
                  <p className="text-lg font-bold text-yellow-400">
                    {chartPatterns.length > 0
                      ? (
                          chartPatterns.reduce((sum, p) => sum + p.confidence, 0) /
                          chartPatterns.length
                        ).toFixed(1)
                      : 0}
                    %
                  </p>
                </div>

                <div className="text-center p-3 bg-blue-500/10 rounded-lg border border-blue-500/30">
                  <Activity className="h-6 w-6 text-blue-400 mx-auto mb-1" />
                  <p className="text-sm text-gray-400">Processing Speed</p>
                  <p className="text-lg font-bold text-blue-400">2.3s</p>
                </div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Chart Analysis Display */}
      {selectedImage && (
        <Card className="bg-gradient-to-r from-gray-900/20 to-slate-900/20 border-gray-500/30 backdrop-blur-xl">
          <CardHeader>
            <CardTitle className="text-white flex items-center">
              <Search className="h-6 w-6 mr-2 text-gray-400" />
              Chart Analysis Results
              <Badge className="ml-3 bg-gradient-to-r from-gray-500 to-slate-500">
                <Layers className="h-3 w-3 mr-1" />
                Computer Vision
              </Badge>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="relative">
              <img
                src={selectedImage || '/placeholder.svg'}
                alt="Chart Analysis"
                className="w-full max-h-96 object-contain rounded-lg"
              />
              <canvas
                ref={canvasRef}
                className="absolute top-0 left-0 w-full h-full pointer-events-none"
                width={800}
                height={400}
              />
            </div>
          </CardContent>
        </Card>
      )}

      {/* Detected Patterns */}
      <Card className="bg-gradient-to-r from-green-900/20 to-emerald-900/20 border-green-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-white flex items-center">
            <BarChart3 className="h-6 w-6 mr-2 text-green-400" />
            Detected Chart Patterns
