interface NeuralAnalysis {
  marketSentiment?: {
    overall: number;
    bullish: number;
    bearish: number;
    neutral: number;
    confidence: number;
  };
  volatilityPrediction?: {
    current: number;
    predicted: number;
    trend: string;
    accuracy: number;
  };
  momentumAnalysis?: {
    shortTerm: string;
    mediumTerm: string;
    longTerm: string;
    strength: number;
  };
  riskAssessment?: {
    level: string;
    score: number;
    factors: string[];
    mitigation: number;
  };
}

interface DeepLearningModel {
  id: number;
  name: string;
  type: string;
  accuracy: number;
  layers: number;
  parameters: string;
  specialty: string;
  status: string;
  processing: string;
}

interface MarketPattern {
  id: number;
  name: string;
  confidence: number;
  timeframe: string;
  symbol: string;
  direction: string;
  completion: number;
  target: number;
  probability: number;
}

interface NeuralPrediction {
  symbol: string;
  neuralScore: number;
  priceTarget: number;
  timeframe: string;
  confidence: number;
  direction: string;
  volatility: number;
  momentum: number;
  riskLevel: string;
