// Type definitions
interface SentimentData {
  [symbol: string]: {
    overall: number;
    bullish: number;
    bearish: number;
    neutral: number;
    volume: number;
    change24h: number;
    momentum: 'increasing' | 'decreasing';
    confidence: number;
  };
}

interface SocialMediaPlatform {
  name: string;
  icon: React.ComponentType<React.SVGProps<SVGSVGElement>>;
  color: string;
  mentions: number;
  sentiment: number;
  engagement: number;
  trending: boolean;
  change: number;
  topKeywords: string[];
}

interface NewsItem {
  id: number;
  headline: string;
  source: string;
  sentiment: string;
  impact: string;
  confidence: number;
  timestamp: Date;
  relevance: number;
}

interface InfluencerData {
  name: string;
  followers: string;
  platform: string;
  sentiment: string;
  confidence: number;
  lastPost: Date;
  engagement: number;
  marketImpact: string;
}

interface MarketMood {
  dominant: string;
  marketPhase: string;
  fearGreedIndex: number;
  volatilityIndex: number;
  crowdSentiment: number;
  riskAppetite: string;
  institutionalFlow: string;
}

