  accuracy: number;
  ordersPlaced: number;
  ordersFilled: number;
  currentPnl: number;
}

interface Strategy {
  name: string;
  type: string;
  performance: number;
  winRate: number;
  description?: string;
  sharpeRatio?: number;
  capacity?: number;
}

interface DataSource {
  name: string;
  type: string;
  status: string;
  latency: string;
  accuracy?: number;
}

interface Model {
  name: string;
  type: string;
  accuracy: number;
  status: string;
  actionSpace?: string;
  performance?: {
    winRate?: number;
    avgReturn?: number;
    sharpeRatio?: number;
  };
}

interface Environment {
  name: string;
  type: string;
  status: string;
  performance: number;
  assets?: string[];
  episodeLength?: number;
  lookbackWindow?: number;
  transactionCosts?: number;
