interface HFTData {
  stats?: any;
  algorithms?: Algorithm[];
  strategies?: Strategy[];
}

interface AltData {
  sources?: DataSource[];
  sampleData?: any;
}

interface RLData {
  agents?: Agent[];
  models?: Model[];
  environments?: Environment[];
}

interface UltimateAIDashboardProps {
