'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { Input } from '@/components/ui/input';
import {
  Bell,
  AlertTriangle,
  TrendingUp,
  Volume2,
  Target,
  Brain,
  Zap,
  Settings,
  X,
  Plus,
} from 'lucide-react';

import { LucideIcon } from 'lucide-react';

interface Alert {
  id: number;
  type: string;
  icon: LucideIcon;
  color: string;
  title: string;
  symbol: string;
  message: string;
  timestamp: Date;
  priority: string;
  read: boolean;
}

interface AlertSettings {
  priceAlerts: boolean;
  volumeAlerts: boolean;
  newsAlerts: boolean;
  aiSignals: boolean;
  optionsFlow: boolean;
  earnings: boolean;
}

export default function RealTimeAlerts() {
  const [alerts, setAlerts] = useState<Alert[]>([]);
  const [alertSettings, setAlertSettings] = useState<AlertSettings>({
    priceAlerts: true,
    volumeAlerts: true,
    newsAlerts: true,
    aiSignals: true,
    optionsFlow: true,
    earnings: true,
  });
  const [customAlerts] = useState<Alert[]>([]);

  useEffect(() => {
    // Simulate real-time alerts
    const interval = setInterval(() => {
      generateNewAlert();
    }, 3000);

    return () => clearInterval(interval);
  }, [alertSettings]);

  const generateNewAlert = () => {
    const alertTypes = [
      {
        type: 'price_breakout',
        icon: TrendingUp,
        color: 'text-emerald-400',
        title: 'Price Breakout',
        enabled: alertSettings.priceAlerts,
      },
      {
        type: 'volume_spike',
        icon: Volume2,
        color: 'text-blue-400',
        title: 'Volume Spike',
        enabled: alertSettings.volumeAlerts,
      },
      {
        type: 'ai_signal',
        icon: Brain,
        color: 'text-purple-400',
        title: 'AI Signal',
        enabled: alertSettings.aiSignals,
      },
      {
        type: 'news_impact',
        icon: AlertTriangle,
        color: 'text-amber-400',
        title: 'News Impact',
        enabled: alertSettings.newsAlerts,
      },
      {
        type: 'options_flow',
        icon: Target,
        color: 'text-orange-400',
        title: 'Options Flow',
        enabled: alertSettings.optionsFlow,
      },
    ];

    const enabledAlerts = alertTypes.filter(alert => alert.enabled);
    if (enabledAlerts.length === 0) return;

    const alertType = enabledAlerts[Math.floor(Math.random() * enabledAlerts.length)];
    const symbols = ['AAPL', 'MSFT', 'GOOGL', 'TSLA', 'NVDA', 'META', 'AMZN', 'NFLX'];
    const symbol = symbols[Math.floor(Math.random() * symbols.length)];

    const messages: Record<string, string[]> = {
      price_breakout: [
        `${symbol} broke above resistance at $${(100 + Math.random() * 400).toFixed(2)}`,
        `${symbol} bullish breakout with strong volume confirmation`,
        `${symbol} breaking out of consolidation pattern`,
      ],
      volume_spike: [
        `${symbol} volume spike: ${(Math.random() * 10 + 2).toFixed(1)}x average`,
        `Unusual volume detected in ${symbol}`,
        `${symbol} seeing heavy institutional activity`,
      ],
      ai_signal: [
        `AI detected strong buy signal for ${symbol}`,
        `${symbol} AI confidence score: ${(80 + Math.random() * 20).toFixed(0)}%`,
        `Machine learning model flagged ${symbol} for momentum`,
      ],
      news_impact: [
        `Breaking news affecting ${symbol} - positive sentiment`,
        `${symbol} mentioned in major financial news`,
        `Analyst upgrade for ${symbol} driving momentum`,
      ],
      options_flow: [
        `Large call buying detected in ${symbol}`,
        `${symbol} unusual options activity - bullish flow`,
        `Whale options trade in ${symbol}: $${(Math.random() * 10 + 1).toFixed(1)}M`,
      ],
    };

    const newAlert = {
      id: Date.now(),
      type: alertType.type,
      icon: alertType.icon,
      color: alertType.color,
      title: alertType.title,
      symbol,
      message:
        messages[alertType.type][Math.floor(Math.random() * messages[alertType.type].length)],
      timestamp: new Date(),
      priority: Math.random() > 0.7 ? 'high' : Math.random() > 0.4 ? 'medium' : 'low',
      read: false,
    };

    setAlerts(prev => [newAlert, ...prev.slice(0, 19)]); // Keep last 20 alerts
  };

  const dismissAlert = (alertId: number) => {
    setAlerts(prev => prev.filter(alert => alert.id !== alertId));
  };

  const markAsRead = (alertId: number) => {
    setAlerts(prev =>
      prev.map((alert: any) => (alert.id === alertId ? { ...alert, read: true } : alert))
    );
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'border-red-500/50 bg-red-500/10';
      case 'medium':
        return 'border-amber-500/50 bg-amber-500/10';
      default:
        return 'border-emerald-500/50 bg-emerald-500/10';
    }
  };

  const unreadCount = alerts.filter(alert => !alert.read).length;

  return (
    <div className="space-y-6">
      {/* Alert Settings */}
      <Card className="bg-stone-900/40 border-emerald-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-stone-100 flex items-center">
            <Settings className="h-6 w-6 mr-2 text-emerald-400" />
            Alert Settings
            {unreadCount > 0 && <Badge className="ml-3 bg-red-500">{unreadCount} new</Badge>}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            {Object.entries(alertSettings).map(([key, enabled]) => (
              <div
                key={key}
                className="flex items-center justify-between p-3 bg-stone-800/30 rounded-lg"
              >
                <span className="text-stone-200 capitalize">
                  {key.replace(/([A-Z])/g, ' $1').trim()}
                </span>
                <Switch
                  checked={enabled}
                  onCheckedChange={checked =>
                    setAlertSettings(prev => ({ ...prev, [key]: checked }))
                  }
                  className="data-[state=checked]:bg-emerald-500"
                />
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Live Alerts Feed */}
      <Card className="bg-stone-900/40 border-emerald-500/30 backdrop-blur-xl">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-stone-100 flex items-center">
              <Bell className="h-6 w-6 mr-2 text-emerald-400" />
              Live Alerts Feed
              <Badge className="ml-3 bg-gradient-to-r from-emerald-500 to-green-600">
                <Zap className="h-3 w-3 mr-1" />
                Real-time
              </Badge>
            </CardTitle>
            <Button
              variant="outline"
              size="sm"
              className="border-stone-600/30 text-stone-300"
              onClick={() => setAlerts([])}
            >
              Clear All
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {alerts.length === 0 ? (
            <div className="text-center py-8">
              <Bell className="h-12 w-12 text-stone-400 mx-auto mb-4" />
              <p className="text-stone-400">
                No alerts yet. Configure your settings above to start receiving alerts.
              </p>
            </div>
          ) : (
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {alerts.map(alert => {
                const IconComponent = alert.icon;
                return (
                  <div
                    key={alert.id}
                    className={`p-4 rounded-lg border transition-all cursor-pointer ${
                      alert.read
                        ? 'bg-stone-800/20 border-stone-600/30'
                        : getPriorityColor(alert.priority)
                    }`}
                    onClick={() => markAsRead(alert.id)}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3">
                        <div className={`p-2 rounded-full bg-stone-800/50`}>
                          <IconComponent className={`h-4 w-4 ${alert.color}`} />
                        </div>
                        <div className="flex-1">
                          <div className="flex items-center space-x-2 mb-1">
                            <span className="text-stone-100 font-semibold">{alert.title}</span>
                            <Badge variant="outline" className="border-stone-600/30 text-stone-400">
                              {alert.symbol}
                            </Badge>
                            <Badge
                              className={
                                alert.priority === 'high'
                                  ? 'bg-red-500'
                                  : alert.priority === 'medium'
                                    ? 'bg-amber-500'
                                    : 'bg-emerald-500'
                              }
                            >
                              {alert.priority}
                            </Badge>
                          </div>
                          <p className="text-stone-300 text-sm">{alert.message}</p>
                          <p className="text-stone-500 text-xs mt-1">
                            {alert.timestamp.toLocaleTimeString()}
                          </p>
                        </div>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={e => {
                          e.stopPropagation();
                          dismissAlert(alert.id);
                        }}
                        className="text-stone-400 hover:text-stone-200"
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Custom Alert Builder */}
      <Card className="bg-stone-900/40 border-emerald-500/30 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-stone-100 flex items-center">
            <Plus className="h-6 w-6 mr-2 text-blue-400" />
            Custom Alert Builder
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <Input
              placeholder="Symbol (e.g., AAPL)"
              className="bg-stone-800/30 border-stone-600/30 text-stone-200"
            />
            <Input
              placeholder="Price Target"
              type="number"
              className="bg-stone-800/30 border-stone-600/30 text-stone-200"
            />
            <select className="bg-stone-800/30 border border-stone-600/30 rounded-md px-3 py-2 text-stone-200">
              <option>Price Above</option>
              <option>Price Below</option>
              <option>Volume Spike</option>
              <option>RSI Oversold</option>
              <option>RSI Overbought</option>
            </select>
            <Button className="bg-gradient-to-r from-emerald-600 to-green-700 hover:from-emerald-700 hover:to-green-800">
              <Plus className="h-4 w-4 mr-2" />
              Create Alert
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState } from 'react';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { LogIn, Mail, Lock, AlertCircle } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

interface SignInFormProps {
  onSignIn: (data: { email: string; name: string }) => void;
  onSwitchToSignUp: () => void;
  onForgotPassword: () => void;
}

export default function SignInForm({
  onSignIn,
  onSwitchToSignUp,
  onForgotPassword,
}: SignInFormProps) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      // Simulate authentication delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Simple validation
      if (!email || !password) {
        throw new Error('Please enter both email and password');
      }

      if (!email.includes('@')) {
        throw new Error('Please enter a valid email address');
      }

      // In a real app, you would call an authentication API here
      // For demo purposes, we'll accept any valid-looking input
      onSignIn({ email, name: email.split('@')[0] });
    } catch (err) {
      const errorMessage =
        err instanceof Error ? err.message : 'Failed to sign in. Please try again.';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Card className="w-full max-w-md bg-black/40 border-purple-500/30 backdrop-blur-xl">
      <CardHeader>
        <CardTitle className="text-white text-2xl">Sign In</CardTitle>
        <CardDescription className="text-gray-400">
          Access your AlphaAI StockX account
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          {error && (
            <Alert variant="destructive" className="bg-red-900/20 border-red-500/50 text-red-300">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <div className="space-y-2">
            <Label htmlFor="email" className="text-white">
              Email
            </Label>
            <div className="relative">
              <Mail className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <Input
                id="email"
                type="email"
                placeholder="you@example.com"
                value={email}
                onChange={e => setEmail(e.target.value)}
                className="pl-10 bg-black/20 border-purple-500/30 text-white"
              />
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <Label htmlFor="password" className="text-white">
                Password
              </Label>
              <Button
                variant="link"
                className="text-purple-400 hover:text-purple-300 p-0 h-auto text-xs"
                onClick={onForgotPassword}
                type="button"
              >
                Forgot password?
              </Button>
            </div>
            <div className="relative">
              <Lock className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <Input
                id="password"
                type="password"
                placeholder="••••••••"
                value={password}
                onChange={e => setPassword(e.target.value)}
                className="pl-10 bg-black/20 border-purple-500/30 text-white"
              />
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox
              id="remember"
              checked={rememberMe}
              onCheckedChange={(checked: boolean) => setRememberMe(!!checked)}
              className="data-[state=checked]:bg-purple-500 data-[state=checked]:border-purple-500"
            />
            <label htmlFor="remember" className="text-sm text-gray-300 cursor-pointer">
              Remember me for 30 days
            </label>
          </div>

          <Button
            type="submit"
            className="w-full bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600"
            disabled={isLoading}
          >
            {isLoading ? (
              <span className="flex items-center">
                <svg
                  className="animate-spin -ml-1 mr-2 h-4 w-4 text-white"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Signing in...
              </span>
            ) : (
              <span className="flex items-center">
                <LogIn className="mr-2 h-4 w-4" />
                Sign In
              </span>
            )}
          </Button>
        </form>
      </CardContent>
      <CardFooter className="flex justify-center border-t border-purple-500/20 pt-4">
        <p className="text-sm text-gray-400">
          Don't have an account?{' '}
          <Button
            variant="link"
            className="text-purple-400 hover:text-purple-300 p-0 h-auto"
            onClick={onSwitchToSignUp}
          >
            Sign up
          </Button>
        </p>
      </CardFooter>
    </Card>
  );
}

