        return 'text-red-400';
      case 'High':
        return 'text-amber-400';
      default:
        return 'text-emerald-400';
    }
  };

  const getRatingColor = (rating: string) => {
    switch (rating) {
      case 'buy':
        return 'text-emerald-400';
      case 'sell':
        return 'text-red-400';
      default:
        return 'text-amber-400';
