// Type definitions for advanced pattern scanner
interface PatternMatch {
  symbol: string;
  pattern: string;
  patternId: string;
  patternType: string;
  category: string;
  currentPrice: number;
  entryPrice: number;
  targetPrice: number;
  stopLoss: number;
  probability: number;
  expectedMove: number;
  riskReward: number;
  timeframe: string;
  detectedAt: Date;
  status: string;
  confidence: number;
  volume: number;
  marketCap: number;
  sector: string;
  assetClass: string;
  direction?: string;
  historicalMatches?: number;
  successRate?: number;
  timeToTarget?: number;
  avgMove?: number;
  strength?: number;
}

interface TradingSignal {
  id: number;
  symbol: string;
  signal: string;
  pattern: string;
  category: string;
  probability: number;
  currentPrice: number;
  entryZone: number[];
  target: number;
  stopLoss: number;
  timeframe: string;
  detectedAt: Date;
  status: string;
  confidence: number;
  riskReward: number;
  avgDaysToTarget: number;
}

interface ScanSettings {
  timeframe: string;
  lookbackPeriod: number;
  minProbability: number;
  patternTypes: string[];
  marketCap: string;
  assetClass: string;
  minVolume: number;
}

interface ChartPattern {
  id: string;
  name: string;
  type: string;
  category: string;
  avgAccuracy: number;
  avgMove: number;
  avgDaysToTarget: number;
  description: string;
  bullishBias: boolean;
  difficultyLevel: string;
  marketConditions: string[];
  tradingStrategy: string;
}

interface PatternStats {
  [key: string]: {
    totalDetected: number;
    successRate: number;
    avgReturn: number;
    avgDaysToTarget: number;
  };
}

