'use client';

import type React from 'react';
import { useState, useEffect } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import { ArrowUp, ArrowDown, AlertTriangle, CheckCircle, XCircle, Loader2 } from 'lucide-react';

interface StockData {
  time: string;
  price: number;
}

interface AlphaWolfBotProps {
  stockSymbol: string;
}

const AlphaWolfBot: React.FC<AlphaWolfBotProps> = ({ stockSymbol }) => {
  const [stockData, setStockData] = useState<StockData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [recommendation, setRecommendation] = useState<'buy' | 'sell' | 'hold' | null>(null);
  const [recommendationConfidence, setRecommendationConfidence] = useState<number | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        // Replace with your actual API endpoint
        const response = await fetch(`/api/trading/analyze?symbol=${stockSymbol}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        if (data && data.stockData && Array.isArray(data.stockData)) {
          setStockData(data.stockData);
        } else {
          console.error('Invalid stock data format:', data);
          setError('Invalid stock data format received.');
          setStockData([]);
        }

        if (data && data.recommendation) {
          setRecommendation(data.recommendation.action);
          setRecommendationConfidence(data.recommendation.confidence);
        } else {
          setRecommendation(null);
          setRecommendationConfidence(null);
        }
      } catch (e: any) {
        console.error('Could not fetch stock data:', e);
        setError(e.message || 'Could not fetch stock data.');
        setStockData([]);
        setRecommendation(null);
        setRecommendationConfidence(null);
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // Refresh data every 60 seconds
    const intervalId = setInterval(fetchData, 60000);

    return () => clearInterval(intervalId); // Cleanup interval on unmount
  }, [stockSymbol]);

  const latestPrice = stockData.length > 0 ? stockData[stockData.length - 1].price : 0;

  let recommendationIcon = null;
  let recommendationText = 'Analyzing...';

  if (loading) {
    recommendationIcon = <Loader2 size={20} className="animate-spin text-blue-500" />;
    recommendationText = 'Analyzing...';
  } else if (error) {
    recommendationIcon = <AlertTriangle size={20} className="text-red-500" />;
    recommendationText = `Error: ${error}`;
  } else if (recommendation === 'buy') {
    recommendationIcon = <ArrowUp size={20} className="text-green-500" />;
    recommendationText = `Buy (Confidence: ${recommendationConfidence?.toFixed(2)}%)`;
  } else if (recommendation === 'sell') {
    recommendationIcon = <ArrowDown size={20} className="text-red-500" />;
    recommendationText = `Sell (Confidence: ${recommendationConfidence?.toFixed(2)}%)`;
  } else if (recommendation === 'hold') {
    recommendationIcon = <CheckCircle size={20} className="text-yellow-500" />;
    recommendationText = `Hold (Confidence: ${recommendationConfidence?.toFixed(2)}%)`;
  } else {
    recommendationIcon = <XCircle size={20} className="text-gray-500" />;
    recommendationText = 'No recommendation';
  }

  return (
    <div className="bg-white shadow rounded-lg p-4">
      <h3 className="text-lg font-semibold text-gray-800 mb-2">
        AlphaWolf Bot Analysis for {stockSymbol}
      </h3>
      <div className="flex items-center mb-2">
        {recommendationIcon}
        <span className="ml-2 text-gray-700">{recommendationText}</span>
      </div>
      {loading ? (
        <div className="flex justify-center items-center h-48">
          <Loader2 size={32} className="animate-spin text-blue-500" />
        </div>
      ) : error ? (
        <div className="text-red-500">Error: {error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={stockData} margin={{ top: 5, right: 20, left: 10, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="time" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="price" stroke="#8884d8" name="Stock Price" />
          </LineChart>
        </ResponsiveContainer>
      )}
      <div className="mt-4 text-sm text-gray-600">Latest Price: ${latestPrice.toFixed(2)}</div>
    </div>
  );
};

export default AlphaWolfBot;

'use client';

import { useState, useEffect, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import {
  Bot,
  Play,
  Pause,
  Square,
  DollarSign,
  TrendingUp,
  Target,
  Shield,
  Rocket,
  Brain,
  Eye,
  Activity,
  Wallet,
  Crown,
  Star,
  CloudLightningIcon as Lightning,
  Atom,
} from 'lucide-react';

// Type definitions
interface User {
  id: string;
  name: string;
  email: string;
}

interface Trade {
  id: string;
  symbol: string;
  side: 'buy' | 'sell';
  quantity: number;
  entryPrice: number;
  currentPrice: number;
  profit: number;
  profitPercent: number;
  timestamp: Date;
  status: 'active' | 'closed';
  // Additional fields used in the component
  entryTime?: Date;
  stopLoss?: number;
  takeProfit?: number;
  confidence?: number;
  strategy?: string;
  pnl?: number;
  positionSize?: number;
}

interface SystemSettings {
  maxRiskPerTrade: number;
  maxDailyRisk: number;
  maxConcurrentTrades: number;
  profitTarget: number;
  stopLoss: number;
  autoScalp: boolean;
  moneyMarketTransfer: boolean;
  minProfitForTransfer: number;
}

interface AIAnalysis {
  marketCondition?: string;
  sentiment?: string;
  volatility?: number;
  riskLevel?: string;
  marketSentiment?: number;
  opportunityScore?: number;
  nextTradeETA?: number;
}

interface ExecutionMetrics {
  totalTrades: number;
  winRate: number;
  avgProfit: number;
  maxDrawdown: number;
  sharpeRatio: number;
  profitFactor: number;
  executionSpeed: number;
  avgWin: number;
  avgLoss: number;
}

interface MarketConditions {
  symbol: string;
  confidence: number;
  trend: string;
  volatility: number;
  price: number;
  signal: string;
  riskLevel: string;
}

interface AutomatedTradingSystemProps {
  user: User;
  membershipLevel: 'free' | 'basic' | 'pro' | 'ultimate';
}

export default function AutomatedTradingSystem({
  user,
  membershipLevel,
}: AutomatedTradingSystemProps) {
  const [systemStatus, setSystemStatus] = useState<'stopped' | 'running' | 'paused'>('stopped');
  const [tradingCapital, setTradingCapital] = useState<number>(50000);
  const [moneyMarketBalance, setMoneyMarketBalance] = useState<number>(0);
  const [totalProfit, setTotalProfit] = useState<number>(0);
  const [dailyProfit, setDailyProfit] = useState<number>(0);
  const [activeTrades, setActiveTrades] = useState<Trade[]>([]);
  const [systemSettings, setSystemSettings] = useState<SystemSettings>({
    maxRiskPerTrade: 2.0,
    maxDailyRisk: 5.0,
    maxConcurrentTrades: 5,
    profitTarget: 10.0,
    stopLoss: 3.0,
    autoScalp: true,
    moneyMarketTransfer: true,
    minProfitForTransfer: 1000,
  });
  const [aiAnalysis, setAiAnalysis] = useState<AIAnalysis>({});
  const [executionMetrics, setExecutionMetrics] = useState<ExecutionMetrics>({
    totalTrades: 0,
    winRate: 0,
    avgProfit: 0,
    maxDrawdown: 0,
    sharpeRatio: 0,
    profitFactor: 0,
    executionSpeed: 25,
    avgWin: 0,
    avgLoss: 0,
  });

  // Premium features check
  const isPremium = ['pro', 'ultimate', 'owner', 'investor'].includes(membershipLevel);

  useEffect(() => {
    if (systemStatus === 'running' && isPremium) {
      const tradingInterval = setInterval(() => {
        executeAutomatedTrading();
        updateAIAnalysis();
        updateExecutionMetrics();
      }, 2000);

      const profitInterval = setInterval(() => {
        calculateInterest();
      }, 10000); // Calculate interest every 10 seconds for demo

      return () => {
        clearInterval(tradingInterval);
        clearInterval(profitInterval);
      };
    }
  }, [systemStatus, isPremium, systemSettings]);

  const executeAutomatedTrading = () => {
    // AI Market Analysis
    const marketConditions = analyzeMarketConditions();

    // Only trade if conditions are favorable
    if (
      marketConditions.confidence > 75 &&
      activeTrades.length < systemSettings.maxConcurrentTrades
    ) {
      const shouldTrade = Math.random() > 0.92; // 8% chance per cycle for realistic trading

      if (shouldTrade) {
        executeTrade(marketConditions);
      }
    }

    // Manage existing trades
    manageActiveTrades();
  };

  const analyzeMarketConditions = () => {
    const symbols = [
      'AAPL',
      'MSFT',
      'GOOGL',
      'TSLA',
      'NVDA',
      'META',
      'AMZN',
      'SPY',
      'QQQ',
      'AMD',
      'CRM',
      'NFLX',
    ];
    const selectedSymbol = symbols[Math.floor(Math.random() * symbols.length)];

    return {
      symbol: selectedSymbol,
      confidence: 70 + Math.random() * 30,
      direction: Math.random() > 0.55 ? 'BUY' : 'SELL', // Slight bullish bias
      volatility: 10 + Math.random() * 30,
      volume: 80 + Math.random() * 40,
      momentum: 50 + Math.random() * 50,
      rsi: 30 + Math.random() * 40,
      macdSignal: Math.random() > 0.6 ? 'bullish' : 'bearish',
      supportResistance: {
        support: 100 + Math.random() * 200,
        resistance: 150 + Math.random() * 300,
      },
    };
  };

  const executeTrade = (conditions: any) => {
    const price = 100 + Math.random() * 400;
    const positionSize = Math.min(
      (tradingCapital * systemSettings.maxRiskPerTrade) / 100,
      tradingCapital * 0.1 // Max 10% of capital per trade
    );
    const quantity = Math.floor(positionSize / price);

    if (quantity > 0 && positionSize <= tradingCapital) {
      const newTrade: Trade = {
        id: Date.now().toString(),
        symbol: conditions.symbol,
        side: conditions.direction.toLowerCase() as 'buy' | 'sell',
        quantity,
        entryPrice: price,
        currentPrice: price,
        profit: 0,
        profitPercent: 0,
        timestamp: new Date(),
        status: 'active',
        positionSize,
        entryTime: new Date(),
        stopLoss: conditions.direction === 'BUY' ? price * 0.97 : price * 1.03,
        takeProfit: conditions.direction === 'BUY' ? price * 1.1 : price * 0.9,
        confidence: conditions.confidence,
        strategy: 'AI Automated',
        pnl: 0,
      };

      setActiveTrades(prev => [...prev, newTrade]);
      setTradingCapital(prev => prev - positionSize);

      // Update metrics
      setExecutionMetrics(prev => ({
        ...prev,
        totalTrades: prev.totalTrades + 1,
        executionSpeed: 15 + Math.random() * 35, // 15-50ms execution
      }));
    }
  };

  const manageActiveTrades = () => {
    setActiveTrades(prevTrades => {
      return prevTrades
        .map(trade => {
          // Update current price with realistic movement
          const priceChange = (Math.random() - 0.5) * trade.entryPrice * 0.02; // ±2% max movement
          const newPrice = Math.max(trade.currentPrice + priceChange, 1);

          // Calculate P&L
          const pnl =
            trade.side === 'buy'
              ? (newPrice - trade.entryPrice) * trade.quantity
              : (trade.entryPrice - newPrice) * trade.quantity;

          const profit = pnl;
          const profitPercent = (pnl / (trade.entryPrice * trade.quantity)) * 100;

          const updatedTrade: Trade = {
            ...trade,
            currentPrice: newPrice,
            pnl,
            profit,
            profitPercent,
          };

          // Check exit conditions
          const shouldExit =
            (updatedTrade.stopLoss && newPrice <= updatedTrade.stopLoss) ||
            (updatedTrade.takeProfit && newPrice >= updatedTrade.takeProfit) ||
            Math.random() > 0.995; // Random exit for variety

          if (shouldExit) {
            closeTrade(updatedTrade);
            return { ...updatedTrade, status: 'closed' as const };
          }

          return updatedTrade;
        })
        .filter(trade => trade.status === 'active');
    });
  };

  const closeTrade = (trade: Trade) => {
    const profit = trade.pnl || 0;
    const isWin = profit > 0;

    // Update capital and profits
    setTradingCapital(prev => prev + (trade.positionSize || 0) + profit);
    setTotalProfit(prev => prev + profit);
    setDailyProfit(prev => prev + profit);

    // Update execution metrics
    setExecutionMetrics(prev => {
      const totalTrades = prev.totalTrades;
      const currentWins = (prev.winRate / 100) * (totalTrades - 1);
      const newWins = isWin ? currentWins + 1 : currentWins;
      const newWinRate = (newWins / totalTrades) * 100;

      return {
        ...prev,
        winRate: newWinRate,
        avgProfit: (prev.avgProfit * (totalTrades - 1) + profit) / totalTrades,
        avgWin: isWin ? (prev.avgWin + profit) / 2 : prev.avgWin,
        avgLoss: !isWin ? (prev.avgLoss + Math.abs(profit)) / 2 : prev.avgLoss,
        profitFactor: prev.avgLoss > 0 ? prev.avgWin / prev.avgLoss : 0,
        sharpeRatio: 2.5 + Math.random() * 1.5, // Simulated Sharpe ratio
      };
    });

    // Auto-transfer profits to money market if enabled
    if (systemSettings.moneyMarketTransfer && profit >= systemSettings.minProfitForTransfer) {
      transferToMoneyMarket(profit * 0.8); // Transfer 80% of large profits
    }
  };

  const transferToMoneyMarket = (amount: number) => {
    setMoneyMarketBalance(prev => prev + amount);
    setTradingCapital(prev => prev - amount);
  };

  const calculateInterest = () => {
    if (moneyMarketBalance > 0) {
      const dailyRate = 1.5 / 365 / 100; // 1.5% annual rate
      const interest = moneyMarketBalance * dailyRate * (10 / 86400); // 10 seconds worth of interest
      setMoneyMarketBalance(prev => prev + interest);
      setTotalProfit(prev => prev + interest);
    }
  };

  const updateAIAnalysis = () => {
    setAiAnalysis(prev => ({
      ...prev,
      marketSentiment: 60 + Math.random() * 40,
      volatility: 15 + Math.random() * 25,
      riskLevel: Math.random() > 0.5 ? 'Low' : 'Medium',
      opportunityScore: 75 + Math.random() * 25,
      nextTradeETA: Math.floor(30 + Math.random() * 120),
    }));
  };

  const updateExecutionMetrics = () => {
    setExecutionMetrics(prev => ({
      ...prev,
      maxDrawdown: Math.max(prev.maxDrawdown, Math.random() * 5),
      executionSpeed: 10 + Math.random() * 40,
    }));
  };

  const startSystem = () => {
    if (!isPremium) {
      alert('Automated Trading System requires Premium Membership!');
      return;
    }
    setSystemStatus('running');
  };

  const pauseSystem = () => {
    setSystemStatus('paused');
  };

  const stopSystem = () => {
    setSystemStatus('stopped');
    // Close all active trades
    activeTrades.forEach(trade => closeTrade(trade));
    setActiveTrades([]);
  };

  const emergencyStop = () => {
    setSystemStatus('stopped');
    activeTrades.forEach(trade => closeTrade(trade));
    setActiveTrades([]);
  };

  if (!isPremium) {
    return (
      <Card className="bg-gradient-to-r from-gray-900/90 to-black/90 border-2 border-red-500/50 backdrop-blur-xl">
        <CardHeader>
          <CardTitle className="text-white flex items-center text-2xl">
            <Crown className="h-8 w-8 mr-3 text-yellow-400" />
            🔒 PREMIUM AUTOMATED TRADING SYSTEM
          </CardTitle>
        </CardHeader>
        <CardContent className="text-center py-12">
          <Bot className="h-24 w-24 text-gray-400 mx-auto mb-6" />
          <h3 className="text-2xl font-bold text-white mb-4">Upgrade to Premium</h3>
          <p className="text-gray-300 mb-6">
            Unlock the most advanced AI automated trading system with guaranteed profits and money
            market integration.
          </p>
          <Button className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-8 py-3 text-lg">
            <Crown className="h-5 w-5 mr-2" />
            Upgrade Now
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-8">
      {/* System Control Panel */}
      <Card className="bg-gradient-to-r from-gray-900/90 to-black/90 border-2 border-cyan-400/50 backdrop-blur-xl shadow-2xl shadow-cyan-500/25">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-white flex items-center text-2xl">
              <Bot className="h-8 w-8 mr-3 text-cyan-400 animate-pulse" />
              🤖 AI AUTOMATED TRADING SYSTEM
              <Badge
                className={`ml-4 text-lg px-4 py-2 animate-pulse ${
                  systemStatus === 'running'
                    ? 'bg-gradient-to-r from-green-400 to-emerald-500 text-black'
                    : systemStatus === 'paused'
                      ? 'bg-gradient-to-r from-yellow-400 to-orange-500 text-black'
                      : 'bg-gradient-to-r from-gray-500 to-gray-600 text-white'
                }`}
              >
                {systemStatus === 'running'
                  ? '🟢 LIVE TRADING'
                  : systemStatus === 'paused'
                    ? '⏸️ PAUSED'
                    : '⏹️ STOPPED'}
              </Badge>
            </CardTitle>

            <div className="flex items-center space-x-3">
              {systemStatus === 'stopped' && (
                <Button
                  onClick={startSystem}
                  className="bg-gradient-to-r from-green-400 to-emerald-500 hover:from-green-500 hover:to-emerald-600 text-black font-bold px-6 py-3 text-lg shadow-lg shadow-green-500/50"
                >
                  <Play className="h-5 w-5 mr-2" />
                  START AI TRADING
                </Button>
              )}

              {systemStatus === 'running' && (
                <>
                  <Button
                    onClick={pauseSystem}
