                    <div className="text-right">
                      <p className="text-xs text-gray-400">
                        {trade.timestamp.toLocaleTimeString()}
                      </p>
                      <div className="mt-1">
                        <Badge
                          variant="outline"
                          className="border-green-500/30 text-green-400 text-xs"
                        >
                          SL: ${trade.stopLoss.toFixed(2)}
                        </Badge>
