            <div className="space-y-2 max-h-64 overflow-y-auto">
              {tradeHistory.slice(0, 10).map((trade: any) => (
                <div
                  key={trade.id}
                  className="flex items-center justify-between p-3 bg-gray-800/30 rounded border border-gray-700/30"
                >
