interface RiskSettings {
  maxPositionSize: number;
  stopLoss: number;
  takeProfit: number;
  maxDailyLoss: number;
}

interface PerformanceMetrics {
  totalReturn: number;
  dailyReturn: number;
  weeklyReturn: number;
  monthlyReturn: number;
  sharpeRatio: number;
  maxDrawdown: number;
  winRate: number;
  lastUpdated: Date;
}

interface PortfolioAnalysis {
  totalValue: number;
  totalPnL: number;
  totalCash: number;
  totalEquity: number;
  positions: PositionAnalysis[];
  dayChange: number;
  dayChangePercent: number;
  allocation: Record<string, any>;
  metrics: {
    totalReturn: number;
    annualizedReturn: number;
