  private getVolatility(symbol: string): number {
    const volatilities: Record<string, number> = {
      AAPL: 0.25,
      MSFT: 0.22,
      GOOGL: 0.28,
      TSLA: 0.45,
      NVDA: 0.35,
      META: 0.32,
