  async startTraining(agentId: string, episodes = 1000): Promise<any> {
    if (this.isTraining) {
      throw new Error('Training is already in progress');
    }

    const agent = this.agents.get(agentId);
    if (!agent) {
      throw new Error(`Agent ${agentId} not found`);
    }

    this.isTraining = true;
    agent.status = 'training';

    console.log(`Starting training for agent ${agent.name}...`);

    // Initialize training progress
    this.trainingProgress.set(agentId, {
      startTime: new Date(),
      targetEpisodes: episodes,
      currentEpisode: 0,
      bestReward: agent.bestReward,
      recentRewards: [],
      averageReward: agent.averageReward,
      explorationRate: agent.explorationRate,
      losses: [],
      convergence: false,
    });

    // Simulate training process
    await this.simulateTraining(agentId, episodes);

    this.isTraining = false;
    agent.status = 'trained';

    return {
      agentId,
      success: true,
      finalEpisode: episodes,
      finalReward: this.trainingProgress.get(agentId).bestReward,
      trainingTime: Date.now() - this.trainingProgress.get(agentId).startTime.getTime(),
    };
  }

  private async simulateTraining(agentId: string, episodes: number): Promise<void> {
    const progress = this.trainingProgress.get(agentId);
    const agent = this.agents.get(agentId);

    for (let episode = 1; episode <= episodes; episode++) {
      // Simulate episode training
      const reward = this.simulateEpisode(agent, episode);

      // Update progress
      progress.currentEpisode = episode;
      progress.recentRewards.push(reward);
      if (progress.recentRewards.length > 100) {
        progress.recentRewards.shift();
      }

      // Update best reward
      if (reward > progress.bestReward) {
        progress.bestReward = reward;
      }

      // Update average reward
      progress.averageReward =
        progress.recentRewards.reduce((a: number, b: number) => a + b, 0) /
        progress.recentRewards.length;

      // Update exploration rate (epsilon decay)
      progress.explorationRate = Math.max(0.01, progress.explorationRate * 0.9995);

      // Simulate loss
      const loss = Math.random() * 0.1 + 0.01;
      progress.losses.push(loss);
      if (progress.losses.length > 100) {
        progress.losses.shift();
      }

      // Check for convergence
      if (episode > 500 && progress.recentRewards.length >= 100) {
        const recentAvg =
          progress.recentRewards.slice(-50).reduce((a: number, b: number) => a + b, 0) / 50;
        const olderAvg =
          progress.recentRewards.slice(-100, -50).reduce((a: number, b: number) => a + b, 0) / 50;
        if (Math.abs(recentAvg - olderAvg) < 0.01) {
          progress.convergence = true;
        }
      }

      // Update agent
      agent.currentEpisode = episode;
      agent.bestReward = progress.bestReward;
      agent.averageReward = progress.averageReward;
      agent.explorationRate = progress.explorationRate;
      agent.lastUpdated = new Date();

      // Simulate training delay
      await new Promise(resolve => setTimeout(resolve, 10));

      // Break if converged early
      if (progress.convergence && episode > 1000) {
        console.log(`Training converged early at episode ${episode}`);
        break;
      }
    }
  }

  private simulateEpisode(agent: any, episode: number): number {
    // Simulate a training episode and return reward
    const model = this.models.get(agent.model);
    const environment = this.environments.get(agent.environment);

    // Base reward with some randomness and improvement over time
    const baseReward = model.performance.sharpeRatio * (0.8 + Math.random() * 0.4);

    // Improvement over time (learning curve)
    const learningProgress = Math.min(episode / 5000, 1.0);
    const improvementFactor = 0.5 + 0.5 * learningProgress;

    // Add some noise and occasional spikes/dips
    const noise = (Math.random() - 0.5) * 0.5;
    const spike = Math.random() > 0.95 ? (Math.random() - 0.5) * 2 : 0;

    return baseReward * improvementFactor + noise + spike;
  }

  async getAgentPrediction(agentId: string, marketData: any): Promise<any> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      throw new Error(`Agent ${agentId} not found`);
    }

    if (agent.status !== 'trained') {
      throw new Error(`Agent ${agentId} is not trained yet`);
    }

    // Simulate agent prediction
    const model = this.models.get(agent.model);
    const environment = this.environments.get(agent.environment);

    // Generate prediction based on agent type
    let prediction;

    if (model.type === 'DQN') {
      // Discrete actions
      const actions = ['STRONG_BUY', 'BUY', 'HOLD', 'SELL', 'STRONG_SELL'];
      const actionProbs = [0.15, 0.25, 0.3, 0.2, 0.1].map(p => p * (0.8 + Math.random() * 0.4));
      const maxIndex = actionProbs.indexOf(Math.max(...actionProbs));

      prediction = {
        action: actions[maxIndex],
        confidence: actionProbs[maxIndex],
        actionProbabilities: actions.reduce(
          (acc: Record<string, number>, action: string, i: number) => {
            acc[action] = actionProbs[i];
            return acc;
          },
          {} as Record<string, number>
        ),
        qValues: actionProbs.map(p => p * 10),
      };
    } else if (model.type === 'PPO' || model.type === 'A3C') {
      // Portfolio weights or continuous actions
      const numAssets = environment.assets?.length || 10;
      const weights = Array.from({ length: numAssets }, () => Math.random());
      const sum = weights.reduce((a, b) => a + b, 0);
      const normalizedWeights = weights.map(w => w / sum);

      prediction = {
        portfolioWeights: normalizedWeights,
        expectedReturn: 0.08 + Math.random() * 0.12,
        expectedVolatility: 0.12 + Math.random() * 0.08,
        confidence: 0.7 + Math.random() * 0.25,
        rebalanceSignal: Math.random() > 0.7,
      };
    } else if (model.type === 'SAC' || model.type === 'TD3') {
      // Continuous actions
      const action = (Math.random() - 0.5) * 2; // -1 to 1
      const position = Math.tanh(action); // Squash to valid range

      prediction = {
        position,
        confidence: 0.6 + Math.random() * 0.3,
        expectedReturn: position * (0.05 + Math.random() * 0.1),
        riskScore: Math.abs(position) * (0.1 + Math.random() * 0.1),
        actionValue: action,
      };
    }

    return {
      agentId,
      agentName: agent.name,
      timestamp: new Date(),
      prediction,
      modelType: model.type,
      environment: environment.name,
      performance: agent.performance,
    };
  }

  async runBacktest(agentId: string, startDate: string, endDate: string): Promise<any> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      throw new Error(`Agent ${agentId} not found`);
    }

    console.log(`Running backtest for agent ${agent.name} from ${startDate} to ${endDate}...`);

    // Simulate backtest results
    const tradingDays = 252; // Approximate trading days per year
    const returns = [];
    let cumulativeReturn = 0;
    let maxDrawdown = 0;
    let currentDrawdown = 0;
    let wins = 0;
    let losses = 0;

    // Generate daily returns
    for (let i = 0; i < tradingDays; i++) {
      const dailyReturn = (Math.random() - 0.45) * 0.03; // Slightly positive bias
      returns.push(dailyReturn);
      cumulativeReturn += dailyReturn;

      // Calculate drawdown
      if (cumulativeReturn < 0) {
        currentDrawdown = Math.abs(cumulativeReturn);
        maxDrawdown = Math.max(maxDrawdown, currentDrawdown);
      } else {
        currentDrawdown = 0;
      }

      // Count wins/losses
      if (dailyReturn > 0) wins++;
      else if (dailyReturn < 0) losses++;
    }

    const totalReturn = cumulativeReturn;
    const volatility =
      Math.sqrt(returns.reduce((sum, r) => sum + r * r, 0) / returns.length) * Math.sqrt(252);
    const sharpeRatio = totalReturn / volatility;
    const winRate = wins / (wins + losses);
    const profitFactor = wins > 0 && losses > 0 ? (wins * 0.01) / (losses * 0.01) : 1.0;

    const backtest = {
      agentId,
      agentName: agent.name,
      period: { startDate, endDate },
      results: {
        totalReturn,
        annualizedReturn: totalReturn,
        volatility,
        sharpeRatio,
        maxDrawdown,
        winRate,
        profitFactor,
        totalTrades: wins + losses,
        winningTrades: wins,
        losingTrades: losses,
        averageWin: 0.012,
        averageLoss: -0.008,
        largestWin: 0.045,
        largestLoss: -0.032,
      },
      dailyReturns: returns,
      equityCurve: returns.reduce((acc: number[], ret: number, i: number) => {
        const prevValue = i === 0 ? 100000 : acc[i - 1];
        acc.push(prevValue * (1 + ret));
        return acc;
      }, [] as number[]),
      timestamp: new Date(),
    };

    // Store backtest results
    this.trainingHistory.set(`backtest_${agentId}_${Date.now()}`, backtest);

    return backtest;
  }

  async getTrainingProgress(agentId: string): Promise<any> {
    const progress = this.trainingProgress.get(agentId);
    if (!progress) {
      return null;
    }

    return {
      ...progress,
      elapsedTime: Date.now() - progress.startTime.getTime(),
      progressPercent: (progress.currentEpisode / progress.targetEpisodes) * 100,
      estimatedTimeRemaining:
        progress.currentEpisode > 0
          ? ((Date.now() - progress.startTime.getTime()) / progress.currentEpisode) *
            (progress.targetEpisodes - progress.currentEpisode)
          : null,
    };
  }

  getModels(): any[] {
    return Array.from(this.models.values());
  }

  getEnvironments(): any[] {
    return Array.from(this.environments.values());
  }

  getAgents(): any[] {
    return Array.from(this.agents.values());
  }

  getAgent(agentId: string): any {
    return this.agents.get(agentId);
  }

  getTrainingHistory(limit = 10): any[] {
    return Array.from(this.trainingHistory.values()).slice(-limit);
  }

  isCurrentlyTraining(): boolean {
    return this.isTraining;
  }
}

export const reinforcementLearningService = ReinforcementLearningService.getInstance();

// Comprehensive Trading Education Service - Series 6 & 7 Test Prep
export class TradingEducationService {
  private static instance: TradingEducationService;
  private educationalContent: Map<string, Record<string, any>> = new Map();
  private testQuestions: Map<string, any[]> = new Map();
  private userProgress: Map<string, any> = new Map();

  static getInstance(): TradingEducationService {
    if (!TradingEducationService.instance) {
      TradingEducationService.instance = new TradingEducationService();
    }
    return TradingEducationService.instance;
  }

  async initialize() {
    this.loadTestQuestions();
  }

  private loadTestQuestions() {
    const series6Questions = [
      {
        id: 's6_q1',
        question: 'Which of the following is NOT a characteristic of mutual funds?',
        options: [
          'Professional management',
          'Daily liquidity',
          'Guaranteed returns',
          'Diversification',
        ],
        correct: 2,
        explanation: 'Mutual funds do not guarantee returns.',
      },
    ];

    const series7Questions = [
      {
        id: 's7_q1',
        question:
          'If XYZ stock is trading at $50 and has a P/E ratio of 20, what are the earnings per share?',
        options: ['$1.00', '$2.50', '$4.00', '$10.00'],
        correct: 1,
        explanation: 'EPS = Stock Price / P/E Ratio = $50 / 20 = $2.50',
      },
    ];

    this.testQuestions.set('series_6', series6Questions);
    this.testQuestions.set('series_7', series7Questions);
  }

  getEducationalContent(series: string, topic?: string) {
    const content = this.educationalContent.get(series);
    if (topic && content) {
      return content[topic] || null;
    }
    return content;
  }

  getTestQuestions(series: string, count = 10) {
    const questions = this.testQuestions.get(series) || [];
    return questions.slice(0, count);
  }

  submitTestAnswer(userId: string, questionId: string, answer: number) {
    if (!this.userProgress.has(userId)) {
      this.userProgress.set(userId, { correct: 0, total: 0, questions: [] });
    }

    const progress = this.userProgress.get(userId);
    const question = this.findQuestionById(questionId);

    if (question) {
      const isCorrect = answer === question.correct;
      progress.total++;
      if (isCorrect) progress.correct++;

      progress.questions.push({
        questionId,
        answer,
        correct: isCorrect,
        timestamp: new Date(),
      });
    }

    return {
      correct: question?.correct === answer,
      explanation: question?.explanation,
      score: (progress.correct / progress.total) * 100,
    };
  }

  private findQuestionById(questionId: string) {
    for (const [, questions] of this.testQuestions) {
      const question = questions.find((q: any) => q.id === questionId);
      if (question) return question;
    }
    return null;
  }

  getUserProgress(userId: string) {
    return this.userProgress.get(userId) || { correct: 0, total: 0, questions: [] };
  }

  generateStudyPlan(userId: string, targetExam: string) {
    const progress = this.getUserProgress(userId);
    const weakAreas = this.identifyWeakAreas(progress, targetExam);

    return {
      targetExam,
      estimatedStudyTime: this.calculateStudyTime(progress, targetExam),
      weeklyPlan: this.createWeeklyPlan(weakAreas, targetExam),
      practiceTests: this.recommendPracticeTests(targetExam),
      keyTopics: this.getKeyTopics(targetExam),
    };
  }

  private identifyWeakAreas(progress: any, _targetExam: string): string[] {
    const weakAreas: string[] = [];

    if (progress && progress.questions && progress.questions.length > 0) {
      const incorrectQuestions = progress.questions.filter((q: any) => !q.correct);
      if (incorrectQuestions.length > 0) {
        weakAreas.push('Review incorrect topics');
      }
    }

    return weakAreas;
  }

  private calculateStudyTime(progress: any, targetExam: string) {
    const baseHours = targetExam === 'series_7' ? 80 : 40;
    const currentScore = progress.total > 0 ? (progress.correct / progress.total) * 100 : 0;

    if (currentScore >= 80) return Math.max(baseHours * 0.5, 20);
    if (currentScore >= 60) return baseHours * 0.75;
    return baseHours;
  }

  private createWeeklyPlan(_weakAreas: string[], _targetExam: string) {
    return [
      { week: 1, topics: ['Securities Basics', 'Market Structure'], hours: 10 },
      { week: 2, topics: ['Equity Securities', 'Debt Securities'], hours: 12 },
      { week: 3, topics: ['Options', 'Investment Companies'], hours: 12 },
      { week: 4, topics: ['Regulations', 'Ethics'], hours: 10 },
      { week: 5, topics: ['Practice Tests', 'Review'], hours: 8 },
    ];
  }

  private recommendPracticeTests(_targetExam: string) {
    return [
      { name: 'Diagnostic Test', questions: 50, timeLimit: 90 },
      { name: 'Mid-term Practice', questions: 100, timeLimit: 180 },
      { name: 'Final Practice', questions: 125, timeLimit: 225 },
      { name: 'Simulated Exam', questions: 125, timeLimit: 225 },
    ];
  }

  private getKeyTopics(targetExam: string): string[] {
    const topics: Record<string, string[]> = {
      series_6: [
        'Investment Company Products (50%)',
        'Securities and Tax Regulations (25%)',
        'Customer Accounts and Prohibited Activities (25%)',
      ],
      series_7: [
        'Seeks Business for the Broker-Dealer (9%)',
        "Opens Accounts After Obtaining and Evaluating Customers' Financial Profile (11%)",
        'Provides Customers with Information About Investments (31%)',
        "Obtains and Verifies Customers' Purchase and Sales Instructions (16%)",
        'Processes Transactions (4%)',
        'Maintains Customer Accounts (29%)',
      ],
    };

    return topics[targetExam] || [];
  }
}

export const tradingEducationService = TradingEducationService.getInstance();

