// API Route for AI IPO Radar;
import { NextRequest, NextResponse } from 'next/server';
import { AIIPORadar } from '@/app/services/ai-tools/ipo-radar';

const ipoRadar = new AIIPORadar();

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const action = searchParams.get('action');
    const ipoId = searchParams.get('ipoId');
    const minScore = searchParams.get('minScore');

    switch (action) {
      case 'all': {
        const allIPOs = await ipoRadar.getAllIPOs();
        return NextResponse.json({
          success: true,
          data: allIPOs,
          count: allIPOs.length,
          timestamp: new Date().toISOString(),
        });
      }
      case 'upcoming': {
        const upcomingIPOs = await ipoRadar.getUpcomingIPOs();
        return NextResponse.json({
          success: true,
          data: upcomingIPOs,
          count: upcomingIPOs.length,
          timestamp: new Date().toISOString(),
        });
      }
      case 'analyze': {
        if (!ipoId) {
          return NextResponse.json({ error: 'IPO ID is required for analysis' }, { status: 400 });
        }
        const analysis = await ipoRadar.analyzeIPO(ipoId);
        return NextResponse.json({
          success: true,
          data: analysis,
          timestamp: new Date().toISOString(),
        });
      }
      case 'top_rated': {
        const scoreThreshold = minScore ? parseInt(minScore) : 70;
        const topRated = await ipoRadar.getIPOsByScore(scoreThreshold);
        return NextResponse.json({
          success: true,
          data: topRated,
          count: topRated.length,
          minScore: scoreThreshold,
          timestamp: new Date().toISOString(),
        });
      }
      case 'scan': {
        const opportunities = await ipoRadar.scanForIPOOpportunities();
        return NextResponse.json({
          success: true,
          data: opportunities,
          count: opportunities.length,
          timestamp: new Date().toISOString(),
        });
      }
      default:;
        return NextResponse.json({ error: 'Invalid action. Supported actions: all, upcoming, analyze, top_rated, scan' }, { status: 400 });
    }
  } catch (error) {
    console.error('IPO Radar API Error:', error);
    return NextResponse.json({
      error: 'Failed to process request',
      details: error instanceof Error ? error.message : 'Unknown error',
    }, { status: 500 });
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { action, ipoData } = body;

    switch (action) {
      case 'add':;
      case 'add':
        if (!ipoData) {
          return NextResponse.json({ error: 'IPO data is required' }, { status: 400 });
        }
        // Validate required fields;
        const requiredFields = ['id', 'companyName', 'sector', 'valuation', 'priceRange'];
        for (const field of requiredFields) {
          if (!ipoData[field]) {
            return NextResponse.json({ error: `Missing required field: ${field}` }, { status: 400 });
          }
        }
        ipoRadar.addIPO(ipoData);
        return NextResponse.json({
          ipoId: ipoData.id,
          timestamp: new Date().toISOString(),
        });

      case 'bulk_analyze':;
        const ipoIds = body.ipoIds || [];
        const analyses: any[] = [];
        for (const id of ipoIds) {
          try {
            const analysis = await ipoRadar.analyzeIPO(id);
            analyses.push(analysis);
          } catch (error) {
            console.error(`Error analyzing IPO ${id}:`, error);
          }
        }
        return NextResponse.json({
          success: true,
          data: analyses,
          count: analyses.length,
          timestamp: new Date().toISOString(),
        });

      default:;
        return NextResponse.json({ error: 'Invalid action. Supported actions: add, bulk_analyze' }, { status: 400 });
    }
  } catch (error) {
    console.error('IPO Radar POST API Error:', error);
    return NextResponse.json({ error: 'Failed to process request' }, { status: 500 });
  }
}

export async function DELETE(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const ipoId = searchParams.get('ipoId');

    if (!ipoId) {
      return NextResponse.json({ error: 'IPO ID is required' }, { status: 400 });
    }

    const removed = ipoRadar.removeIPO(ipoId);

    if (removed) {
      return NextResponse.json({ success: true, message: 'IPO removed successfully' }, { status: 200 });
    } else {
      return NextResponse.json({ error: 'IPO not found' }, { status: 404 });
    }
  } catch (error) {
    console.error('IPO Radar DELETE API Error:', error);
    return NextResponse.json({ error: 'Failed to remove IPO' }, { status: 500 });
  }
}

// API Route for AI Portfolio Optimizer
import { NextRequest, NextResponse } from 'next/server';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { action, investmentAmount, constraints, currentHoldings } = body;

    switch (action) {
      case 'optimize': {
        if (!investmentAmount || !constraints) {
          return NextResponse.json({ error: 'Investment amount and constraints are required' }, { status: 400 });
        }
        // Validate constraints
        const requiredConstraints = [
          'riskTolerance',
          'minWeight',
          'maxWeight',
          'maxSectorAllocation'
        ];
        for (const field of requiredConstraints) {
          if (constraints[field] === undefined) {
            return NextResponse.json({ error: `Missing required constraint: ${field}` }, { status: 400 });
          }
        }

        // Mock optimized portfolio
        const optimizedPortfolio = {
          allocations: [
            { symbol: 'AAPL', weight: 0.3, expectedReturn: 0.12 },
            { symbol: 'NVDA', weight: 0.2, expectedReturn: 0.18 },
            { symbol: 'MSFT', weight: 0.25, expectedReturn: 0.14 },
            { symbol: 'TSLA', weight: 0.15, expectedReturn: 0.22 },
            { symbol: 'BONDS', weight: 0.1, expectedReturn: 0.05 }
          ],
          expectedReturn: 0.142,
          risk: 0.18,
          sharpeRatio: 0.78
        };

        return NextResponse.json({
          success: true,
          data: optimizedPortfolio,
          timestamp: new Date().toISOString()
        });
      }
      case 'backtest': {
        const { allocations, timeframe } = body;
        if (!allocations) {
          return NextResponse.json({ error: 'Portfolio allocations are required for backtesting' }, { status: 400 });
        }

        // Mock backtest results
        const backtestResults = {
          totalReturn: 0.234,
          annualizedReturn: 0.145,
          volatility: 0.18,
          sharpeRatio: 0.81,
          maxDrawdown: -0.12,
          winRate: 0.68
        };

        return NextResponse.json({
          success: true,
          data: backtestResults,
          timeframe: timeframe || '1Y',
          timestamp: new Date().toISOString()
        });
      }
      case 'recommendations': {
        const { riskTolerance, investmentGoals } = body;
        if (!riskTolerance) {
          return NextResponse.json({ error: 'Risk tolerance is required for recommendations' }, { status: 400 });
        }

        // Mock recommendations
        const recommendations = [
          { symbol: 'AAPL', reason: 'Strong fundamentals', score: 95 },
          { symbol: 'NVDA', reason: 'AI growth potential', score: 92 },
          { symbol: 'MSFT', reason: 'Cloud dominance', score: 89 }
        ];

        return NextResponse.json({
          success: true,
