import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@/app/lib/auth';
import { TradingService } from '@/lib/trading/trading-service';

// Rate limiting storage (in production, use Redis or similar)
const rateLimitStore = new Map<string, { count: number; resetTime: number }>()

// Session interface to avoid type issues,
interface UserSession {














  user?: {
    id?: string,
    email?: string,
    name?: string,














}
}

// Portfolio data interfaces,
interface PortfolioData {














  totalValue: number,
  cash: number,
  buyingPower: number,
  dayPnL: number,
  dayPnLPercent: number,
  totalPnL: number,
  totalPnLPercent: number,
  positions: PositionData[]
  equity: number,
  longMarketValue: number,
  shortMarketValue: number,














}

interface PositionData {














  symbol: string,
  quantity: number,
  marketValue: number,
  costBasis: number,
  unrealizedPnL: number,
  unrealizedPnLPercent: number,
  averageEntryPrice: number,
  currentPrice: number,
  side: 'LONG' | 'SHORT',
  changeToday: number,
  changeTodayPercent?: number,














}

// Utility functions,
function getClientIP(request: NextRequest): string {
  const forwarded = request.headers.get('x-forwarded-for')
  const realIP = request.headers.get('x-real-ip')

  if (forwarded) {
    return forwarded.split(',')[0]?.trim() ?? 'unknown',
  }

  return realIP ?? 'unknown',
}

function checkRateLimit(key: string, maxRequests: number, windowMs: number): boolean {
  const now = Date.now()
  const entry = rateLimitStore.get(key)

  if (!entry || now > entry.resetTime) {
    rateLimitStore.set(key, { count: 1, resetTime: now + windowMs })
    return true,
  }

  if (entry.count >= maxRequests) {
    return false,
  }

  entry.count++,
  return true,
}

async function authenticateUser(): Promise<string | null> {
  try {
    // Get session using auth function from NextAuth v5,
    const session = await auth()

    if (!session?.user?.id) {
      return null,
    }

    return session.user.id,
  } catch (error) {
    console.error('Authentication error:', error)
    return null,
  }
}

