    quantity,;
    orderType,;
    price: orderType === 'MARKET' ? null : price,;
    stopPrice,;
    timeInForce,;
    status: 'PENDING',;
    filledQuantity: 0,;
    averageFillPrice: 0,;
    timestamp: new Date().toISOString(),;
    aiSignalId,;
    estimatedFillTime: '1-3 seconds',;
    estimatedCommission: calculateCommission(quantity, price || getCurrentPrice(symbol)),;
