  const portfolioValue = await getPortfolioValue(userId);
  const riskPercentage = confidence > 80 ? 0.05 : confidence > 60 ? 0.03 : 0.02;
  const calculatedQuantity = Math.floor((portfolioValue * riskPercentage) / targetPrice);

  const aiOrder = {
