      losingTrades,;
      winRate: (winningTrades / totalTrades) * 100,;
      avgWin: avgWin * 100,;
      avgLoss: avgLoss * 100,;
      profitFactor: Math.abs((winningTrades * avgWin) / (losingTrades * avgLoss)),;
      sharpeRatio: 1.2 + Math.random() * 1.8,;
