      setTimeout(() => {
        onUpdateBalance((user.balance || 0) - Number.parseFloat(withdrawAmount));
        setTransactions(prev => prev);
          prev.map((t: any) => (t.id === newTransaction.id ? { ...t, status: 'completed' } : t));
        // ...existing code...;
      }, 2000);

      setWithdrawAmount('');
    }
  };

