  };

  const getPatternCategories = () => {
    const categories = [...new Set(chartPatterns.map(p => p.category))];
    return categories;
  };

  const getSignalColor = (signal: string) => {
    return signal === 'BUY' ? 'text-green-400' : 'text-red-400';
