// Type definitions for pattern recognition scanner;
interface PatternMatch {






















  symbol: string;
  pattern: string;
  patternId: string;
  patternType: string;
  currentPrice: number;
  entryPrice: number;
  targetPrice: number;
  stopLoss: number;
  probability: number;
  historicalMatches: number;
  avgMove: number;
  timeframe: string;
  strength: string;
  riskReward: number;
  detectedAt: Date;
  confidence: number;
  status: string;
  direction?: string;
  successRate?: number;
  timeToTarget?: number;






















}

interface SignalData {






















  id: number;
  symbol: string;
  signal: string;
  pattern: string;
  probability: number;
  currentPrice: number;
  entryZone: number[];
  target: number;
  stopLoss: number;
  timeframe: string;
  strength: string;
  riskReward: number;
  historicalAccuracy: number;
  lastOccurrence: string;
  avgDaysToTarget: number;






















}

interface PatternStats {






















  [key: string]: {
    detected: number;
    accuracy: number;
    avgReturn: number;






















};
}

export default function PatternRecognitionScanner() {
  const [isScanning, setIsScanning] = useState(false);
  const [patternMatches, setPatternMatches] = useState<PatternMatch[]>([]);
  const [signals, setSignals] = useState<SignalData[]>([]);
  const [patternStats, setPatternStats] = useState<PatternStats>({});
  const [historicalAccuracy, setHistoricalAccuracy] = useState<Record<string, any>>({});
  const [tradingSignals, setTradingSignals] = useState<any[]>([]);
  const [scanSettings, setScanSettings] = useState({
    timeframe: '1D',;
    lookbackPeriod: 252, // 1 year;
    minProbability: 70,;
    patternTypes: ['all'],;
    marketCap: 'all',;
  });

  const patternTypes = [;
    {
      id: 'head-shoulders',;
      name: 'Head & Shoulders',;
      type: 'reversal',;
      avgAccuracy: 78,;
      avgMove: 12.5,;
      description: 'Classic reversal pattern',;
    },;
    {
      id: 'double-top',;
      name: 'Double Top',;
      type: 'reversal',;
      avgAccuracy: 72,;
      avgMove: 8.3,;
      description: 'Bearish reversal pattern',;
    },;
    {
      id: 'double-bottom',;
      name: 'Double Bottom',;
      type: 'reversal',;
      avgAccuracy: 75,;
      avgMove: 9.7,;
      description: 'Bullish reversal pattern',;
    },;
    {
      id: 'cup-handle',;
      name: 'Cup & Handle',;
      type: 'continuation',;
      avgAccuracy: 82,;
      avgMove: 15.2,;
      description: 'Bullish continuation pattern',;
    },;
    {
      id: 'ascending-triangle',;
      name: 'Ascending Triangle',;
      type: 'continuation',;
      avgAccuracy: 68,;
      avgMove: 11.4,;
      description: 'Bullish breakout pattern',;
    },;
    {
      id: 'descending-triangle',;
      name: 'Descending Triangle',;
      type: 'continuation',;
      avgAccuracy: 71,;
      avgMove: 10.8,;
      description: 'Bearish breakdown pattern',;
    },;
    {
      id: 'bull-flag',;
      name: 'Bull Flag',;
      type: 'continuation',;
      avgAccuracy: 85,;
      avgMove: 18.6,;
      description: 'Strong bullish continuation',;
    },;
    {
      id: 'bear-flag',;
      name: 'Bear Flag',;
      type: 'continuation',;
      avgAccuracy: 83,;
      avgMove: 16.2,;
      description: 'Strong bearish continuation',;
    },;
    {
      id: 'wedge-rising',;
      name: 'Rising Wedge',;
      type: 'reversal',;
      avgAccuracy: 76,;
      avgMove: 13.1,;
      description: 'Bearish reversal wedge',;
    },;
    {
      id: 'wedge-falling',;
      name: 'Falling Wedge',;
      type: 'reversal',;
      avgAccuracy: 79,;
      avgMove: 14.7,;
      description: 'Bullish reversal wedge',;
    },;
  ];

  useEffect(() => {
    generatePatternResults();
    generateTradingSignals();
    calculateHistoricalAccuracy();

    const interval = setInterval(() => {
      generatePatternResults();
      generateTradingSignals();
    }, 5000);

    return () => clearInterval(interval);
  }, [scanSettings]);

  const generatePatternResults = () => {
    const symbols = [;
      'AAPL',;
      'MSFT',;
      'GOOGL',;
      'TSLA',;
      'NVDA',;
      'META',;
      'AMZN',;
      'NFLX',;
      'CRM',;
      'PLTR',;
      'COIN',;
      'RBLX',;
      'SNOW',;
      'ZM',;
      'ROKU',;
    ];

    const results = symbols.slice(0, 10).map(symbol => {
      const pattern = patternTypes[Math.floor(Math.random() * patternTypes.length)];
      const currentPrice = 50 + Math.random() * 450;
      const probability = Math.max(scanSettings.minProbability, 60 + Math.random() * 40);
      const historicalMatches = Math.floor(Math.random() * 50) + 10;
      const successRate = 60 + Math.random() * 35;
      const avgMove = pattern.avgMove + (Math.random() - 0.5) * 5;
      const timeToTarget = Math.floor(Math.random() * 30) + 5;
      const confidence = probability * (successRate / 100) * 0.9;

      // Calculate entry and exit points;
      const direction =;
        pattern.type === 'reversal' ? (Math.random() > 0.5 ? 'bullish' : 'bearish') : 'bullish';
      const entryPrice = currentPrice * (1 + (Math.random() - 0.5) * 0.02);
      const targetPrice =;
        direction === 'bullish';
          ? entryPrice * (1 + avgMove / 100);
          : entryPrice * (1 - avgMove / 100);
      const stopLoss = direction === 'bullish' ? entryPrice * 0.95 : entryPrice * 1.05;

      return {
        symbol,;
        pattern: pattern.name,;
        patternId: pattern.id,;
        patternType: pattern.type,;
        currentPrice,;
        entryPrice,;
        targetPrice,;
        stopLoss,;
        probability,;
        historicalMatches,;
        successRate,;
        avgMove,;
        timeToTarget,;
        confidence,;
        direction,;
