'use client';
import React, { useEffect, useRef } from 'react';

interface Particle {






















  x: number;
  y: number;
  vx: number;
  vy: number;
  size: number;
  opacity: number;
  color: string;






















}

export default function AnimatedBackground() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particlesRef = useRef<Particle[]>([]);
  const animationFrameRef = useRef<number>();
  const lastCanvasSize = useRef<{ width: number; height: number }>({ width: 0, height: 0 });
  const resizeTimeout = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Debounced resize;
    const handleResize = () => {
      if (resizeTimeout.current !== null) clearTimeout(resizeTimeout.current);
      resizeTimeout.current = setTimeout(() => {
        resizeCanvas();
        // Only recreate particles if canvas size actually changed;
        if (;
          canvas.width !== lastCanvasSize.current.width ||;
          canvas.height !== lastCanvasSize.current.height;
        ) {
          createParticles();
        }
      }, 100);
    };

    const resizeCanvas = () => {
      const dpr = window.devicePixelRatio || 1;
      const width = window.innerWidth;
      const height = window.innerHeight;
      canvas.width = width * dpr;
      canvas.height = height * dpr;
      canvas.style.width = width + 'px';
      canvas.style.height = height + 'px';
      ctx.setTransform(1, 0, 0, 1, 0, 0); // Reset transform;
      ctx.scale(dpr, dpr);
      lastCanvasSize.current = { width: canvas.width, height: canvas.height };
    };

    const createParticles = () => {
      const particles: Particle[] = [];
      const colors = ['#8B5CF6', '#06B6D4', '#10B981', '#F59E0B', '#EF4444'];
      const width = window.innerWidth;
      const height = window.innerHeight;
      for (let i = 0; i < 50; i++) {
        particles.push({
          x: Math.random() * width,;
          y: Math.random() * height,;
          vx: (Math.random() - 0.5) * 0.5,;
          vy: (Math.random() - 0.5) * 0.5,;
          size: Math.random() * 3 + 1,;
          opacity: Math.random() * 0.5 + 0.2,;
          color: colors[Math.floor(Math.random() * colors.length)],;
        });
      }
      particlesRef.current = particles;
    };

    const drawParticles = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      ctx.clearRect(0, 0, width, height);
      const particles = particlesRef.current;
      for (let i = 0; i < particles.length; i++) {
        const particle = particles[i];
        // Update position;
        particle.x += particle.vx;
        particle.y += particle.vy;
        // Bounce off edges;
        if (particle.x <= 0 || particle.x >= width) particle.vx *= -1;
        if (particle.y <= 0 || particle.y >= height) particle.vy *= -1;
        // Draw particle;
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = particle.color;
        ctx.globalAlpha = particle.opacity;
import { AnimatedBackground } from "../../../components/ui/animated-background-client";
        ctx.fill();
        // Draw connections to up to 3 nearest particles only;
        let connections = 0;
        for (let j = 0; j < particles.length && connections < 3; j++) {
          if (i !== j) {
            const other = particles[j];
            const dx = particle.x - other.x;
            const dy = particle.y - other.y;
            const distance = Math.sqrt(dx * dx + dy * dy);
            if (distance < 100) {
              ctx.beginPath();
              ctx.moveTo(particle.x, particle.y);
              ctx.lineTo(other.x, other.y);
              ctx.strokeStyle = particle.color;
              ctx.globalAlpha = ((100 - distance) / 100) * 0.2;
              ctx.lineWidth = 0.5;
              ctx.stroke();
              connections++;
            }
          }
        }
      }
      ctx.globalAlpha = 1;
    };

    const animate = () => {
      drawParticles();
      animationFrameRef.current = window.requestAnimationFrame(animate);
    };

    resizeCanvas();
    createParticles();
    animate();
    window.addEventListener('resize', handleResize, { passive: true });

    return () => {
      window.removeEventListener('resize', handleResize);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      if (resizeTimeout.current !== null) {
        clearTimeout(resizeTimeout.current);
      }
    };
  }, []);

  return (;
    <canvas;
      ref={canvasRef}
      className="fixed inset-0 pointer-events-none z-0";
      style={{ background: 'transparent' }}
    />;
  );
}

