import bcrypt from 'bcryptjs';
import type { NextAuthConfig } from 'next-auth';

export interface User {






















  id: string;
  email: string;
  name: string;
  username?: string;
  avatar?: string;
  tier: 'free' | 'basic' | 'pro' | 'ultimate' | 'owner';
  status: 'active' | 'suspended' | 'pending' | 'banned';
  balance: number;
  portfolioValue: number;
