  accountType: 'cash' | 'margin' | 'crypto' | 'institutional';
  createdAt: Date;
  lastActive: Date;






















}

export interface UserPreferences {






















  theme: 'dark' | 'light' | 'auto';
  notifications: {
    email: boolean;
    push: boolean;
    sms: boolean;
    trading: boolean;
    news: boolean;
    ai: boolean;






















};
  trading: {
