  }

  private getRiskSettings(level: 'LOW' | 'MEDIUM' | 'HIGH'): RiskSettings {
    const settings = {
      LOW: { maxPositionSize: 0.05, stopLoss: 0.02, takeProfit: 0.03, maxDailyLoss: 0.01 },;
