import { getSession } from 'next-auth/react';

// API Base Configuration;
const API_BASE_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3000';

interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
  message?: string;
}

class ApiClient {
  private baseURL: string;

  constructor(baseURL: string = API_BASE_URL) {
    this.baseURL = baseURL;
  }

  private async getAuthHeaders(): Promise<HeadersInit> {
    const session = await getSession();
    return {
      'Content-Type': 'application/json',;
      ...(session?.user && { Authorization: `Bearer ${session.user.id}` }),;
    };
  }

  private async request<T>(endpoint: string, options: RequestInit = {}): Promise<ApiResponse<T>> {
    try {
      const headers = await this.getAuthHeaders();
      const response = await fetch(`${this.baseURL}${endpoint}`, {
        ...options,;
        headers: {
          ...headers,;
          ...options.headers,;
        },;
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || `HTTP error! status: ${response.status}`);
      }

      return data;
    } catch (error) {
      console.error(`API request failed for ${endpoint}:`, error);
      return {
        success: false,;
        error: error instanceof Error ? error.message : 'Unknown error',;
      };
    }
  }

  // User Authentication & Profile;
  async registerUser(userData: {
    name: string;
    email: string;
    password: string;
    username?: string;
  }) {
    return this.request('/api/auth/register', {
      method: 'POST',;
      body: JSON.stringify(userData),;
    });
  }

  async getUserProfile() {
    return this.request('/api/user/profile');
  }

  async updateUserProfile(profileData: any) {
    return this.request('/api/user/profile', {
      method: 'PUT',;
      body: JSON.stringify(profileData),;
    });
  }

  // Portfolio Management;
  async getPortfolios(portfolioId?: string) {
    const query = portfolioId ? `?portfolioId=${portfolioId}` : '';
    return this.request(`/api/portfolio${query}`);
  }

  async createPortfolio(portfolioData: {
    name: string;
    description: string;
    type?: string;
    initialDeposit?: number;
  }) {
    return this.request('/api/portfolio', {
      method: 'POST',;
      body: JSON.stringify(portfolioData),;
    });
  }

  // Trading Orders;
  async placeOrder(orderData: {
    symbol: string;
    side: 'BUY' | 'SELL';
