export class HighFrequencyTradingService {
  private static instance: HighFrequencyTradingService;
  private isRunning = false;
  private executionLatency = 0.5; // milliseconds;
  private orderTypes: string[] = [;
    'market',;
    'limit',;
    'iceberg',;
    'twap',;
    'vwap',;
    'pov',;
    'sniper',;
