  );
}

// Enhanced Particle Field with Mouse Interaction;
export function EnhancedParticleField() {
  const [particles, setParticles] = useState<;
    Array<{ id: number; x: number; y: number; vx: number; vy: number; size: number }>;
  >([]);

  useEffect(() => {
    if (typeof window === 'undefined') return;

    const particleCount = 50;
    const newParticles = Array.from({ length: particleCount }, (_, i) => ({
      id: i,;
      x: Math.random() * window.innerWidth,;
      y: Math.random() * window.innerHeight,;
      vx: (Math.random() - 0.5) * 2,;
      vy: (Math.random() - 0.5) * 2,;
      size: Math.random() * 2 + 1,;
    }));

    setParticles(newParticles);

    const interval = setInterval(() => {
      setParticles(prev =>;
        prev.map(particle => ({
          ...particle,;
          x: (particle.x + particle.vx + window.innerWidth) % window.innerWidth,;
          y: (particle.y + particle.vy + window.innerHeight) % window.innerHeight,;
        }));
      );
    }, 50);

    return () => clearInterval(interval);
  }, []);

  return (;
    <div className="fixed inset-0 pointer-events-none -z-10">;
      <svg className="w-full h-full">;
        {particles.map(particle => (;
          <circle;
            key={particle.id}
            cx={particle.x}
            cy={particle.y}
            r={particle.size}
            fill="#3b82f6";
            opacity="0.3";
          />;
        ))}

        {/* Connection lines between nearby particles */}
        {particles.flatMap((particle, i) => {
          return particles.slice(i + 1).map((otherParticle, j) => {
            const distance = Math.sqrt(;
              Math.pow(particle.x - otherParticle.x, 2) + Math.pow(particle.y - otherParticle.y, 2);
            );

            if (distance < 150) {
              return (;
                <line;
                  key={`${i}-${j}`}
                  x1={particle.x}
                  y1={particle.y}
                  x2={otherParticle.x}
                  y2={otherParticle.y}
                  stroke="url(#connectionGradient)";
                  strokeWidth="1";
                  opacity={Math.max(0, 1 - distance / 150)}
                />;
              );
            }
            return null;
          });
        })}

        <defs>;
          <linearGradient id="connectionGradient" x1="0%" y1="0%" x2="100%" y2="100%">;
            <stop offset="0%" stopColor="#3b82f6" stopOpacity="0.5" />;
            <stop offset="100%" stopColor="#ec4899" stopOpacity="0.5" />;
          </linearGradient>;
        </defs>;
      </svg>;
    </div>;
  );
}

