"use client";
import React, { useEffect, useRef } from 'react';

interface Particle {






















  x: number;
  y: number;
  vx: number;
  vy: number;
  size: number;
  color: string;
  opacity: number;






















}

export default function AnimatedBackground() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particlesRef = useRef<Particle[]>([]);
  const animationFrameRef = useRef<number>();

  useEffect(() => {
    if (typeof window === 'undefined') return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      if (typeof window !== 'undefined') {
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
      }
    };

    const createParticles = () => {
      const particles: Particle[] = [];
      const colors = ['#8B5CF6', '#06B6D4', '#10B981', '#F59E0B', '#EF4444'];

      for (let i = 0; i < 50; i++) {
        particles.push({
          x: Math.random() * canvas.width,;
          y: Math.random() * canvas.height,;
          vx: (Math.random() - 0.5) * 0.5,;
          vy: (Math.random() - 0.5) * 0.5,;
          size: Math.random() * 3 + 1,;
          color: colors[Math.floor(Math.random() * colors.length)],;
          opacity: Math.random() * 0.5 + 0.3,;
        });
      }

      particlesRef.current = particles;
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particlesRef.current.forEach(particle => {
        particle.x += particle.vx;
        particle.y += particle.vy;

        if (particle.x < 0 || particle.x > canvas.width) particle.vx *= -1;
        if (particle.y < 0 || particle.y > canvas.height) particle.vy *= -1;

        ctx.save();
        ctx.globalAlpha = particle.opacity;
import { AnimatedBackground } from "./animated-background-client";
        ctx.fillStyle = particle.color;
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fill();
        ctx.restore();
      });

      animationFrameRef.current = requestAnimationFrame(animate);
    };

    resizeCanvas();
    createParticles();
    animate();

    const handleResize = () => {
      resizeCanvas();
      createParticles();
    };

    if (typeof window !== 'undefined') {
      window.addEventListener('resize', handleResize);
    }

    return () => {
      if (typeof window !== 'undefined') {
        window.removeEventListener('resize', handleResize);
      }
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, []);

  return (;
    <canvas;
      ref={canvasRef}
      className="fixed inset-0 pointer-events-none z-0";
      style={{ background: 'transparent' }}
    />;
  );
}

import React from 'react';
('use client');

import { useEffect, useRef, useState } from 'react';
import { cn } from '@/lib/utils';

interface FloatingElementsProps {






















  count?: number;
  className?: string;






















}

export function FloatingElements({ count = 20, className }: FloatingElementsProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [elements, setElements] = useState<;
    Array<{
      id: number;
      x: number;
      y: number;
      size: number;
      speed: number;
      color: string;
      direction: number;
    }>;
  >([]);

  useEffect(() => {
    const colors = [;
      'hsl(217, 91%, 60%)', // neon-blue;
      'hsl(271, 91%, 65%)', // neon-purple;
      'hsl(322, 85%, 70%)', // neon-pink;
      'hsl(142, 76%, 66%)', // neon-green;
      'hsl(189, 85%, 70%)', // neon-cyan;
    ];

    const newElements = Array.from({ length: count }, (_, i) => ({
      id: i,;
      x: Math.random() * 100,;
      y: Math.random() * 100,;
      size: Math.random() * 4 + 2,;
      speed: Math.random() * 0.5 + 0.1,;
      color: colors[Math.floor(Math.random() * colors.length)],;
      direction: Math.random() * Math.PI * 2,;
    }));

    setElements(newElements);
  }, [count]);

  useEffect(() => {
    const interval = setInterval(() => {
      setElements(prev =>;
        prev.map(element => ({
          ...element,;
          x: (element.x + Math.cos(element.direction) * element.speed + 100) % 100,;
          y: (element.y + Math.sin(element.direction) * element.speed + 100) % 100,;
        }));
      );
    }, 50);

    return () => clearInterval(interval);
  }, []);

  return (;
    <div;
      ref={containerRef}
      className={cn('absolute inset-0 overflow-hidden pointer-events-none', className)}
    >;
      {elements.map(element => (;
        <div;
          key={element.id}
          className="absolute rounded-full opacity-30 animate-pulse";
          style={{
            left: `${element.x}%`,;
            top: `${element.y}%`,;
            width: `${element.size}px`,;
            height: `${element.size}px`,;
            backgroundColor: element.color,;
            boxShadow: `0 0 ${element.size * 2}px ${element.color}`,;
          }}
        />;
      ))}
    </div>;
  );
}

interface MorphingBlobProps {






















  className?: string;
  color?: string;
  size?: number;






















}

export function MorphingBlob({
  className,;
  color = 'hsl(217, 91%, 60%)',;
  size = 200,;
}: MorphingBlobProps) {
  const pathRef = useRef<SVGPathElement>(null);

  useEffect(() => {
    const path = pathRef.current;
    if (!path) return;

    let animationId: number;
    let time = 0;

    const animate = () => {
      time += 0.02;

      const points: any[] = [];
      const numPoints = 6;
      const radius = size / 2;
      const centerX = size / 2;
      const centerY = size / 2;

      for (let i = 0; i < numPoints; i++) {
        const angle = (i / numPoints) * Math.PI * 2;
        const variation = Math.sin(time + i) * 0.3 + 0.7;
        const x = centerX + Math.cos(angle) * radius * variation;
        const y = centerY + Math.sin(angle) * radius * variation;
        points.push({ x, y });
      }

      // Create smooth curve through points;
      let pathData = `M ${points[0].x} ${points[0].y}`;

      for (let i = 0; i < points.length; i++) {
        const current = points[i];
        const next = points[(i + 1) % points.length];
        const controlX = current.x + (next.x - current.x) * 0.5;
        const controlY = current.y + (next.y - current.y) * 0.5;

        pathData += ` Q ${controlX} ${controlY} ${next.x} ${next.y}`;
      }

      pathData += ' Z';
      path.setAttribute('d', pathData);

      animationId = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [size]);

  return (;
    <svg;
      width={size}
      height={size}
