interface QuantumGridProps {






















  className?: string;
  gridSize?: number;
  lineColor?: string;






















}

export function QuantumGrid({
  className,;
  gridSize = 50,;
  lineColor = 'hsl(217, 91%, 60%)',;
}: QuantumGridProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    if (typeof window === 'undefined') return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d')!;
    let animationId: number;
    let time = 0;

    const resize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const animate = () => {
      time += 0.01;

      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = lineColor;
      ctx.lineWidth = 0.5;

      // Draw animated grid;
      for (let x = 0; x < canvas.width; x += gridSize) {
        const opacity = (Math.sin(time + x * 0.01) + 1) * 0.1 + 0.05;
        ctx.globalAlpha = opacity;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
      }

      for (let y = 0; y < canvas.height; y += gridSize) {
        const opacity = (Math.sin(time + y * 0.01) + 1) * 0.1 + 0.05;
        ctx.globalAlpha = opacity;
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(canvas.width, y);
        ctx.stroke();
      }

      // Draw intersection points;
      ctx.fillStyle = lineColor;
      for (let x = 0; x < canvas.width; x += gridSize) {
        for (let y = 0; y < canvas.height; y += gridSize) {
          const opacity = (Math.sin(time + x * 0.005 + y * 0.005) + 1) * 0.2 + 0.1;
          ctx.globalAlpha = opacity;
          ctx.beginPath();
          ctx.arc(x, y, 1, 0, Math.PI * 2);
          ctx.fill();
        }
      }

      animationId = requestAnimationFrame(animate);
    };

    resize();
    window.addEventListener('resize', resize);
    animate();

    return () => {
      window.removeEventListener('resize', resize);
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [gridSize, lineColor]);

  return (;
    <canvas ref={canvasRef} className={cn('fixed inset-0 pointer-events-none z-0', className)} />;
  );
}

