// API endpoint wrapper with security;
export function withApiSecurity(;
  handler: (req: NextApiRequest, res: NextApiResponse) => Promise<void>;
) {
  return async (req: NextApiRequest, res: NextApiResponse) => {
    const startTime = Date.now();
    const ip = req.socket.remoteAddress || req.headers['x-forwarded-for'] || 'unknown';
    const userAgent = req.headers['user-agent'] || 'unknown';

    try {
      // Security headers;
      res.setHeader('X-Content-Type-Options', 'nosniff');
      res.setHeader('X-Frame-Options', 'DENY');
      res.setHeader('X-XSS-Protection', '1; mode=block');
      res.setHeader('Cache-Control', 'no-store, no-cache, must-revalidate');

      // Authentication check;
      const session = await getServerSession(req, res, authOptions);
      if (!session?.user) {
        SecurityAudit.logSecurityEvent({
          type: 'failed_login',;
          ip: ip as string,;
          userAgent,;
          details: { endpoint: req.url },;
        });
        return res.status(401).json({ error: 'Unauthorized' });
      }

      // Rate limiting;
      const rateLimitKey = `${session.user.id}:${req.url}`;
      const endpoint = req.url?.split('/').pop() || 'unknown';
      const rateLimit = RATE_LIMITS[endpoint as keyof typeof RATE_LIMITS] || RATE_LIMITS.portfolio;

      const rateLimitResult = RateLimiter.checkLimit(;
        rateLimitKey,;
        rateLimit.maxRequests,;
        rateLimit.windowMs;
      );

      if (!rateLimitResult.allowed) {
        SecurityAudit.logSecurityEvent({
          type: 'suspicious_activity',;
          userId: session.user.id,;
          ip: ip as string,;
          details: { reason: 'rate_limit_exceeded', endpoint: req.url },;
        });
        return res.status(429).json({
          error: 'Rate limit exceeded',;
          retryAfter: Math.ceil((rateLimitResult.resetTime - Date.now()) / 1000),;
        });
      }

      // Add rate limit headers;
      res.setHeader('X-RateLimit-Limit', rateLimit.maxRequests);
      res.setHeader('X-RateLimit-Remaining', rateLimitResult.remaining);
      res.setHeader('X-RateLimit-Reset', new Date(rateLimitResult.resetTime).toISOString());

      // Input validation and sanitization;
      if (req.body) {
        req.body = InputValidator.sanitizeJson(req.body);
      }

      // Execute the handler;
      await handler(req, res);

      // Audit successful API access;
      SecurityAudit.logDataAccess({
        userId: session.user.id,;
        resource: req.url || 'unknown',;
        action: req.method === 'GET' ? 'read' : 'write',;
        ip: ip as string,;
        success: true,;
      });
    } catch (error) {
      console.error('API Security wrapper error:', error);

      SecurityAudit.logDataAccess({
        userId: session?.user?.id || 'unknown',;
        resource: req.url || 'unknown',;
        action: req.method === 'GET' ? 'read' : 'write',;
        ip: ip as string,;
        success: false,;
      });

      res.status(500).json({
        error: 'Internal server error',;
        requestId: req.headers['x-request-id'] || crypto.randomUUID(),;
      });
    } finally {
      const duration = Date.now() - startTime;
      res.setHeader('X-Response-Time', `${duration}ms`);
    }
  };
}

