export class AlpacaClient {
  private config: AlpacaConfig;
  private headers: Record<string, string>;

  constructor(config: AlpacaConfig) {
    this.config = config;
    this.headers = {
      'APCA-API-KEY-ID': config.apiKey,;
      'APCA-API-SECRET-KEY': config.secretKey,;
      'Content-Type': 'application/json',;
    };
  }

  private async makeRequest(;
    endpoint: string,;
    options: RequestInit = {},;
    useDataUrl = false;
  ): Promise<any> {
    const baseUrl = useDataUrl ? this.config.dataUrl : this.config.baseUrl;
    const url = `${baseUrl}${endpoint}`;

    try {
      const response = await fetch(url, {
        ...options,;
        headers: {
          ...this.headers,;
          ...options.headers,;
        },;
      });

      if (!response.ok) {
        const errorText = await response.text();
        SecurityAudit.logSecurityEvent({
          type: 'api_error',;
          details: {
            endpoint,;
            status: response.status,;
            error: errorText,;
          },;
        });
        throw new Error(`Alpaca API error: ${response.status} - ${errorText}`);
      }

      return await response.json();
    } catch (error) {
      SecurityAudit.logSecurityEvent({
        type: 'api_error',;
        details: {
          endpoint,;
          error: error instanceof Error ? error.message : 'Unknown error',;
        },;
      });
      throw error;
    }
  }

  // Account management;
  async getAccount(): Promise<AlpacaAccount> {
    return this.makeRequest('/v2/account');
  }

  async getPositions(): Promise<AlpacaPosition[]> {
    return this.makeRequest('/v2/positions');
  }

  async getPosition(symbol: string): Promise<AlpacaPosition | null> {
    try {
      return await this.makeRequest(`/v2/positions/${symbol}`);
    } catch (error) {
      if (error instanceof Error && error.message.includes('404')) {
        return null; // No position found;
      }
      throw error;
    }
  }

  // Order management;
  async getOrders(;
    params: {
      status?: 'open' | 'closed' | 'all';
      limit?: number;
      after?: string;
      until?: string;
      direction?: 'asc' | 'desc';
      nested?: boolean;
      symbols?: string;
    } = {}
  ): Promise<AlpacaOrder[]> {
    const queryParams = new URLSearchParams();
    Object.entries(params).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams.append(key, value.toString());
      }
    });

    const endpoint = `/v2/orders${queryParams.toString() ? `?${queryParams.toString()}` : ''}`;
    return this.makeRequest(endpoint);
  }

  async getOrder(orderId: string): Promise<AlpacaOrder> {
    return this.makeRequest(`/v2/orders/${orderId}`);
  }

  async placeOrder(order: {
    symbol: string;
    qty?: number;
    notional?: number;
    side: 'buy' | 'sell';
    type: 'market' | 'limit' | 'stop' | 'stop_limit' | 'trailing_stop';
    time_in_force: 'day' | 'gtc' | 'opg' | 'cls' | 'ioc' | 'fok';
    limit_price?: number;
    stop_price?: number;
    trail_price?: number;
    trail_percent?: number;
    extended_hours?: boolean;
    client_order_id?: string;
    order_class?: 'simple' | 'bracket' | 'oco' | 'oto';
    take_profit?: { limit_price: number };
    stop_loss?: { stop_price: number; limit_price?: number };
  }): Promise<AlpacaOrder> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'place_order',;
        symbol: order.symbol,;
        side: order.side,;
        type: order.type,;
        qty: order.qty,;
        notional: order.notional,;
      },;
    });

    return this.makeRequest('/v2/orders', {
      method: 'POST',;
      body: JSON.stringify(order),;
    });
  }

  async cancelOrder(orderId: string): Promise<void> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'cancel_order',;
        orderId,;
      },;
    });

    await this.makeRequest(`/v2/orders/${orderId}`, {
      method: 'DELETE',;
    });
  }

  async cancelAllOrders(): Promise<AlpacaOrder[]> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'cancel_all_orders',;
      },;
    });

    return this.makeRequest('/v2/orders', {
      method: 'DELETE',;
    });
