async function handlePlaceOrder(orderData: any, userId: string) {
  const {
    symbol,;
    side, // BUY or SELL;
    quantity,;
    orderType, // MARKET, LIMIT, STOP, STOP_LIMIT;
    price,;
    stopPrice,;
    timeInForce = 'DAY', // DAY, GTC, IOC, FOK;
