  // Validate order data;
  if (!symbol || !side || !quantity || quantity <= 0) {
    return NextResponse.json({ error: 'Invalid order parameters' }, { status: 400 });
  }

  // Risk checks;
  const riskCheck = await performRiskChecks(orderData, userId);
  if (!riskCheck.approved) {
