interface PortfolioStock {






















  symbol: string;
  weight: number;
  currentPrice: number;
  expectedReturn: number;
  risk: number;






















}


interface OptimizationResult {






















  expectedReturn: number;
  volatility: number;
  risk: number;
  sharpeRatio: number;
  maxDrawdown: number;
  diversificationScore: number;
  recommendations: Array<{
    action: string;
    symbol: string;
    suggestedWeight: number;
    reason: string;
    impact: string;






















}>;
  allocations: Array<{
    symbol: string;
    currentWeight: number;
    optimizedWeight: number;
    allocation: number;
  }>;
  metrics: {
