    try {
      const response = await fetch('/api/ai-tools/portfolio-optimizer', {
        method: 'POST',;
        headers: { 'Content-Type': 'application/json' },;
        body: JSON.stringify({
          riskTolerance: riskTolerance[0],;
          targetReturn: targetReturn[0],;
          timeHorizon: parseInt(timeHorizon),;
          includeRebalancing,;
        }),;
      });

