    <Card className="w-full max-w-md bg-black/40 border-purple-500/30 backdrop-blur-xl">;
      <CardHeader>;
        <CardTitle className="text-white text-2xl">Create Account</CardTitle>;
        <CardDescription className="text-gray-400">;
          Join AlphaAI StockX and start trading;
        </CardDescription>;
      </CardHeader>;
      <CardContent>;
        <form onSubmit={handleSubmit} className="space-y-4">;
          {error && (;
            <Alert variant="destructive" className="bg-red-900/20 border-red-500/50 text-red-300">;
              <AlertCircle className="h-4 w-4" />;
              <AlertDescription>{error}</AlertDescription>;
            </Alert>;
          )}

          <div className="space-y-2">;
            <Label htmlFor="name" className="text-white">;
              Full Name;
            </Label>;
            <div className="relative">;
              <User className="absolute left-3 top-3 h-4 w-4 text-gray-400" />;
              <Input;
                id="name";
                placeholder="John Doe";
                value={name}
                onChange={e => setName(e.target.value)}
                className="pl-10 bg-black/20 border-purple-500/30 text-white";
              />;
            </div>;
          </div>;
          <div className="space-y-2">;
            <Label htmlFor="email" className="text-white">;
              Email;
            </Label>;
            <div className="relative">;
              <Mail className="absolute left-3 top-3 h-4 w-4 text-gray-400" />;
              <Input;
                id="email";
                type="email";
                placeholder="you@example.com";
                value={email}
                onChange={e => setEmail(e.target.value)}
                className="pl-10 bg-black/20 border-purple-500/30 text-white";
              />;
            </div>;
          </div>;
          <div className="space-y-2">;
            <Label htmlFor="password" className="text-white">;
              Password;
            </Label>;
            <div className="relative">;
              <Lock className="absolute left-3 top-3 h-4 w-4 text-gray-400" />;
              <Input;
                id="password";
                type="password";
                placeholder="";
                value={password}
                onChange={e => setPassword(e.target.value)}
                className="pl-10 bg-black/20 border-purple-500/30 text-white";
              />;
            </div>;
          </div>;
          <div className="space-y-2">;
            <Label htmlFor="confirmPassword" className="text-white">;
              Confirm Password;
            </Label>;
            <div className="relative">;
              <Lock className="absolute left-3 top-3 h-4 w-4 text-gray-400" />;
              <Input;
                id="confirmPassword";
                type="password";
                placeholder="";
                value={confirmPassword}
                onChange={e => setConfirmPassword(e.target.value)}
                className="pl-10 bg-black/20 border-purple-500/30 text-white";
              />;
            </div>;
          </div>;
          <div className="flex items-center space-x-2">;
            <Checkbox;
              id="terms";
              checked={agreeTerms}
              onCheckedChange={checked => setAgreeTerms(!!checked)}
              className="data-[state=checked]:bg-purple-500 data-[state=checked]:border-purple-500";
            />;
            <label htmlFor="terms" className="text-sm text-gray-300 cursor-pointer">;
              I agree to the{' '}
              <a href="#" className="text-purple-400 hover:text-purple-300">;
                Terms of Service;
              </a>{' '}
              and{' '}
              <a href="#" className="text-purple-400 hover:text-purple-300">;
                Privacy Policy;
              </a>;
            </label>;
          </div>;
          <Button;
            type="submit";
            className="w-full bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600";
            disabled={isLoading}
          >;
            {isLoading ? (;
              <span className="flex items-center">;
                <svg;
                  className="animate-spin -ml-1 mr-2 h-4 w-4 text-white";
                  xmlns="http://www.w3.org/2000/svg";
                  fill="none";
                  viewBox="0 0 24 24";
                >;
                  <circle;
                    className="opacity-25";
                    cx="12";
                    cy="12";
                    r="10";
                    stroke="currentColor";
                    strokeWidth="4";
                  ></circle>;
                  <path;
                    className="opacity-75";
                    fill="currentColor";
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z";
                  ></path>;
                </svg>;
                Creating account...;
              </span>;
            ) : (;
              <span className="flex items-center">;
                <UserPlus className="mr-2 h-4 w-4" />;
                Create Account;
              </span>;
            )}
          </Button>;
        </form>;
      </CardContent>;
      <CardFooter className="flex justify-center border-t border-purple-500/20 pt-4">;
        <p className="text-sm text-gray-400">;
          Already have an account?{' '}
          <Button;
            variant="link";
            className="text-purple-400 hover:text-purple-300 p-0 h-auto";
            onClick={onSwitchToSignIn}
          >;
            Sign in;
          </Button>;
        </p>;
      </CardFooter>;
    </Card>;
  );
}

