                    </SelectTrigger>;
                    <SelectContent>;
                      {bankAccounts;
                        .filter(acc => acc.verified);
                        .map((account: any) => (;
                          <SelectItem key={account.id} value={account.id.toString()}>;
                            {account.bankName} - {account.accountNumber}
                          </SelectItem>;
                        ))}
                    </SelectContent>;
                  </Select>;
