  const [testResults, setTestResults] = useState<TestResults | null>(null);
  const [studyProgress, setStudyProgress] = useState<StudyProgress>({});

  useEffect(() => {
    const initEducation = async () => {
      try {
        await tradingEducationService.initialize();
        loadTestQuestions();
