// Advanced Interfaces for Next-Gen Trading Platform;
interface User {






















  id: string;
  username: string;
  email: string;
  name: string;
  avatar: string;
  tier: 'free' | 'basic' | 'pro' | 'ultimate' | 'owner';
  status: 'online' | 'trading' | 'offline' | 'away';
  reputation: number;
  followers: number;
  following: number;
  totalTrades: number;
  winRate: number;
  roi: number;
  aiCredits: number;
  badges: string[];
  verified: boolean;
  portfolioValue: number;
  totalPnL: number;
  riskScore: number;
  tradingLevel: number;
  balance: number;
  joinDate: Date;
  lastActive: Date;






















}

interface TradingSignal {






















  id: string;
  symbol: string;
  action: 'BUY' | 'SELL' | 'HOLD';
  confidence: number;
  aiModel: string;
  strategy: string;
  targetPrice: number;
  stopLoss: number;
  timeframe: string;
  reasoning: string[];
  sentiment: number;
  riskLevel: 'LOW' | 'MEDIUM' | 'HIGH';
  timestamp: Date;
  author?: User;
  likes: number;
  comments: number;
  copies: number;
  performance?: number;
  price: number;
  currentPrice: number;






















}

interface SocialPost {






















  id: string;
  author: User;
  content: string;
  signals?: TradingSignal[];
  likes: number;
  comments: number;
  shares: number;
  timestamp: Date;
  symbols: string[];
  tradingIdea?: {
    symbol: string;
    action: string;
    targetPrice: number;
    reasoning: string;






















};
}

interface Portfolio {






















  id: string;
  name: string;
  totalValue: number;
  dayChange: number;
  dayChangePercent: number;
  allTimeReturn: number;
  positions: Position[];
  riskScore: number;
  pnl: number;
  pnlPercent: number;






















}

interface Position {






















  symbol: string;
  quantity: number;
  avgPrice: number;
  currentPrice: number;
  pnl: number;
  pnlPercent: number;
  allocation: number;






















}

interface AIModel {






















  id: string;
  name: string;
  type: 'LSTM' | 'Transformer' | 'GAN' | 'Ensemble';
  accuracy: number;
  performance: number;
  status: 'running' | 'training' | 'optimizing';
  author: User;
  subscribers: number;
  price: number;






















}

interface NextGenPlatformProps {






















  initialUser?: User;






















}

