interface UserProfileProps {






















  user: any;
  onUpdate: (user: any) => void;






















}

export default function UserProfile({ user, onUpdate }: UserProfileProps) {
  const [activeTab, setActiveTab] = useState('profile');
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user.name || '',;
    email: user.email || '',;
    phone: user.phone || '',;
    location: user.location || '',;
    bio: user.bio || '',;
  });
  const [preferences, setPreferences] = useState({
    emailNotifications: true,;
    pushNotifications: true,;
    marketAlerts: true,;
    educationUpdates: true,;
    weeklyReports: true,;
    darkMode: true,;
    autoSave: true,;
    ...user.preferences,;
  });

  const handleSave = () => {
    const updatedUser = {
      ...user,;
      ...formData,;
      preferences,;
    };
    onUpdate(updatedUser);
    setEditing(false);
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handlePreferenceChange = (preference: string, value: boolean) => {
    setPreferences((prev: any) => ({ ...prev, [preference]: value }));
  };

  const getSubscriptionColor = (subscription: string) => {
    const colors: { [key: string]: string } = {
      free: 'bg-slate-600',;
      basic: 'bg-blue-600',;
      premium: 'bg-purple-600',;
      enterprise: 'bg-yellow-600',;
    };
    return colors[subscription] || colors.free;
  };

  return (;
    <div className="space-y-6">;
      {/* Profile Header */}
      <Card className="bg-slate-800/50 backdrop-blur-sm border-blue-500/20">;
        <CardContent className="p-6">;
          <div className="flex items-center justify-between">;
            <div className="flex items-center gap-4">;
              <div className="w-16 h-16 bg-blue-600 rounded-full flex items-center justify-center">;
                <User className="h-8 w-8 text-white" />;
              </div>;
              <div>;
                <h2 className="text-2xl font-bold text-white">{user.name}</h2>;
                <p className="text-slate-400">{user.email}</p>;
                <div className="flex items-center gap-2 mt-2">;
                  <Badge className={getSubscriptionColor(user.subscription)}>;
                    {user.subscription.charAt(0).toUpperCase() + user.subscription.slice(1)}
                  </Badge>;
                  <Badge variant="outline">;
                    <Calendar className="h-3 w-3 mr-1" />;
                    Joined {new Date(user.joinDate).toLocaleDateString()}
                  </Badge>;
                </div>;
              </div>;
            </div>;
            <Button;
              variant={editing ? 'default' : 'outline'}
              onClick={() => setEditing(!editing)}
              className="flex items-center gap-2";
            >;
              {editing ? <Save className="h-4 w-4" /> : <Edit className="h-4 w-4" />}
              {editing ? 'Save Changes' : 'Edit Profile'}
            </Button>;
          </div>;
        </CardContent>;
      </Card>;
      {/* Profile Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">;
        <TabsList className="grid w-full grid-cols-5 bg-slate-800/50 backdrop-blur-sm">;
          <TabsTrigger value="profile" className="flex items-center gap-2">;
            <User className="h-4 w-4" />;
            Profile;
          </TabsTrigger>;
          <TabsTrigger value="preferences" className="flex items-center gap-2">;
            <Settings className="h-4 w-4" />;
            Preferences;
          </TabsTrigger>;
          <TabsTrigger value="notifications" className="flex items-center gap-2">;
            <Bell className="h-4 w-4" />;
            Notifications;
          </TabsTrigger>;
          <TabsTrigger value="security" className="flex items-center gap-2">;
            <Shield className="h-4 w-4" />;
            Security;
          </TabsTrigger>;
          <TabsTrigger value="billing" className="flex items-center gap-2">;
            <CreditCard className="h-4 w-4" />;
            Billing;
          </TabsTrigger>;
        </TabsList>;
        {/* Profile Tab */}
        <TabsContent value="profile" className="space-y-6">;
          <Card className="bg-slate-800/50 backdrop-blur-sm border-blue-500/20">;
            <CardHeader>;
              <CardTitle className="text-white">Personal Information</CardTitle>;
