                    <MapPin className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 h-4 w-4" />;
                    <Input;
                      id="location";
                      value={formData.location}
                      onChange={e => handleInputChange('location', e.target.value)}
