        totalOccurrences: Math.floor(Math.random() * 500) + 100,;
        successfulTrades: Math.floor(Math.random() * 300) + 60,;
        avgReturn: (Math.random() - 0.3) * 20 + 5,;
        avgDaysToTarget: Math.floor(Math.random() * 30) + 10,;
