                      <span className="text-green-400 font-semibold">;
                        {successRate.toFixed(1)}%;
                      </span>;
                    </div>;
                    <div className="flex justify-between">;
                      <span className="text-gray-400 text-sm">Avg Return:</span>;
                      <span;
                        className={`font-semibold ${
