                  <Button variant="outline" size="sm" className="w-full">;
                    <Target className="h-4 w-4 mr-2" />;
                    Set Stop Loss;
                  </Button>;
                  <Button variant="outline" size="sm" className="w-full">;
                    <Shield className="h-4 w-4 mr-2" />;
