        <CardHeader>;
          <div className="flex items-center justify-between">;
            <CardTitle className="text-gray-100 flex items-center">;
              <Target className="h-6 w-6 mr-2 text-cyan-400" />;
              Live Options Trading Bot;
              <Badge;
                className={`ml-3 ${
                  botStatus === 'running';
                    ? 'bg-green-500 animate-pulse';
                    : botStatus === 'paused';
                      ? 'bg-yellow-500';
                      : 'bg-gray-500';
                }`}
              >;
                {botStatus === 'running';
                  ? ' LIVE OPTIONS';
                  : botStatus === 'paused';
                    ? ' PAUSED';
                    : ' STOPPED'}
              </Badge>;
            </CardTitle>;
            <div className="flex items-center space-x-2">;
              {botStatus === 'stopped' && (;
                <Button;
                  onClick={startBot}
                  className="bg-green-500 hover:bg-green-600 text-black font-bold";
                >;
                  <Play className="h-4 w-4 mr-2" />;
                  START OPTIONS BOT;
                </Button>;
              )}

              {botStatus === 'running' && (;
                <>;
                  <Button;
                    onClick={pauseBot}
                    className="bg-yellow-500 hover:bg-yellow-600 text-black";
                  >;
                    <Pause className="h-4 w-4 mr-2" />;
                    Pause;
                  </Button>;
                  <Button;
                    onClick={stopBot}
                    variant="outline";
                    className="border-gray-500 text-gray-300";
                  >;
                    <Square className="h-4 w-4 mr-2" />;
                    Stop;
                  </Button>;
                </>;
              )}

              {botStatus === 'paused' && (;
                <>;
                  <Button;
                    onClick={() => setBotStatus('running')}
                    className="bg-green-500 hover:bg-green-600 text-black";
                  >;
                    <Play className="h-4 w-4 mr-2" />;
                    Resume;
                  </Button>;
                  <Button;
                    onClick={stopBot}
                    variant="outline";
                    className="border-gray-500 text-gray-300";
                  >;
                    <Square className="h-4 w-4 mr-2" />;
                    Stop;
                  </Button>;
                </>;
              )}
            </div>;
          </div>;
        </CardHeader>;
        <CardContent>;
          {/* Strategy Selection */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">;
            <div className="space-y-2">;
              <label className="text-gray-200 text-sm font-medium">Options Strategy</label>;
              <Select;
                value={botSettings.optionsStrategy}
                onValueChange={value =>;
                  setBotSettings(prev => ({ ...prev, optionsStrategy: value }));
                }
              >;
                <SelectTrigger className="bg-gray-800/30 border-cyan-500/30 text-gray-200">;
                  <SelectValue />;
                </SelectTrigger>;
                <SelectContent className="bg-gray-800 border-gray-600">;
                  {optionsStrategies.map((strategy: OptionsStrategy) => (;
                    <SelectItem key={strategy.id} value={strategy.id}>;
                      <div>;
