export default function SportsAlphaTrader() {
  const [activeEvents, setActiveEvents] = useState<SportsEvent[]>([]);
  const [opportunities, setOpportunities] = useState<TradingOpportunity[]>([]);
  const [activeTrades, setActiveTrades] = useState<ActiveTrade[]>([]);
  const [profitStats, setProfitStats] = useState<ProfitStats>({
    totalProfit: 0,;
    dailyProfit: 0,;
    winRate: 0,;
    avgSpread: 0,;
    maxProfit: 0,;
    riskRatio: 0,;
  });
  const [isHunting, setIsHunting] = useState(true);
  const [expandedSections, setExpandedSections] = useState<ExpandedSections>({
    realTime: false,;
    adaptive: false,;
    profit: false,;
    risk: false,;
    interface: false,;
  });

  // Simulate sports events;
  useEffect(() => {
    const sportsEvents = [;
      {
        id: 1,;
        type: 'NFL',;
        teams: 'Chiefs vs. Ravens',;
        time: 'Live - Q3',;
        markets: ['Spread', 'Moneyline', 'Total Points'],;
        volatility: 'High',;
        opportunities: 3,;
      },;
      {
        id: 2,;
        type: 'NBA',;
        teams: 'Lakers vs. Celtics',;
        time: 'Starting in 15m',;
        markets: ['Spread', 'Player Props', 'Quarter Lines'],;
        volatility: 'Medium',;
        opportunities: 2,;
      },;
      {
        id: 3,;
        type: 'MLB',;
        teams: 'Yankees vs. Dodgers',;
        time: 'Live - 6th Inning',;
        markets: ['Run Line', 'Moneyline', 'Inning Props'],;
        volatility: 'Medium',;
        opportunities: 1,;
      },;
      {
        id: 4,;
        type: 'UFC',;
        teams: 'McGregor vs. Poirier',;
        time: 'Starting in 2h',;
        markets: ['Moneyline', 'Round Props', 'Method of Victory'],;
        volatility: 'High',;
        opportunities: 4,;
      },;
      {
        id: 5,;
        type: 'Soccer',;
        teams: 'Man City vs. Liverpool',;
        time: "Live - 65'",;
        markets: ['Asian Handicap', 'Goal Line', 'Corners'],;
        volatility: 'Low',;
        opportunities: 2,;
      },;
    ];

    setActiveEvents(sportsEvents);
    generateOpportunities(sportsEvents);

    const interval = setInterval(() => {
      if (isHunting) {
        generateOpportunities(sportsEvents);
        updateActiveTrades();
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [isHunting]);

  const generateOpportunities = (events: SportsEvent[]) => {
    const newOpportunities: TradingOpportunity[] = [];

    events.forEach((event: SportsEvent) => {
