        const spreadValue = (Math.random() * 10 - 5).toFixed(1);
        const confidence = Math.floor(Math.random() * 30) + 70;
        const expectedValue = (Math.random() * 8 + 2).toFixed(1);
        const timeWindow = ['Immediate', '5 minutes', '15 minutes', '30 minutes'][;
